          r.name as role_name,
          COALESCE(os.is_online, false) as is_online,
          COALESCE(os.last_seen, u.created_at) as last_seen
        FROM users u
        LEFT JOIN online_status os ON u.id = os.user_id
        LEFT JOIN roles r ON u.role_id = r.id
        WHERE COALESCE(os.is_online, false) = true
        ORDER BY r.name, COALESCE(u.first_name, u.name), COALESCE(u.last_name, '')
      `);

      return result.rows.map((row: any) => ({
        id: row.id,
        name: row.first_name && row.last_name ? 
          `${row.first_name} ${row.last_name}` : 
          row.name || `User ${row.id}`,
        email: row.email,
        role: row.role_name || 'user',
        roleId: row.role_id,
        isOnline: row.is_online,
        lastSeen: row.last_seen
      }));
    } catch (error) {
      console.error('Error getting online users:', error);
      return [];
    }
  }

  // System Settings compatibility methods for admin dashboard
  async getSystemSettings(): Promise<SystemSettings | null> {
    try {
      const [settings] = await db.select().from(systemSettings).limit(1);
      return settings || null;
    } catch (error) {
      console.error('Error fetching system settings:', error);
      return null;
    }
  }

  async updateSystemSettings(updates: Partial<InsertSystemSettings>): Promise<SystemSettings> {
    try {
      // Check if settings exist
      const existing = await this.getSystemSettings();

      if (existing) {
        // Update existing settings
        const [updated] = await db
          .update(systemSettings)
          .set({ ...updates, updatedAt: new Date() })
          .where(eq(systemSettings.id, existing.id))
          .returning();
        return updated;
      } else {
        // Create new settings
        const [created] = await db
          .insert(systemSettings)
          .values(updates)
          .returning();
        return created;
      }
    } catch (error) {
      console.error('Error updating system settings:', error);
      throw error;
    }
  }
}

export const storage = new DatabaseStorage();

