      purple: 'bg-purple-100 text-purple-600'
    };
    return colorMap[color as keyof typeof colorMap] || 'bg-gray-100 text-gray-600';
  };

  return (
    <div className="saas-grid">
      {stats.map((stat, index) => (
        <div key={index} className="saas-card saas-scale-in group" data-testid={`stat-card-${index}`}>
          <div className="saas-card-content">
            <div className="flex items-center justify-between mb-4">
              <div className={`w-12 h-12 rounded-xl flex items-center justify-center ${getColorClasses(stat.color)} group-hover:scale-110 transition-transform duration-200`}>
                <i className={`${stat.icon} text-base`}></i>
              </div>
              <div className="text-right">
                <span className="text-2xl font-bold text-gray-900" data-testid={`stat-value-${index}`}>
                  {stat.value}
                </span>
              </div>
            </div>
            <div>
              <h3 className="font-semibold text-gray-900 mb-1" data-testid={`stat-title-${index}`}>
                {stat.title}
              </h3>
              <p className={`text-sm font-medium ${stat.color === 'green' ? 'text-green-600' : stat.color === 'orange' ? 'text-orange-600' : stat.color === 'purple' ? 'text-purple-600' : 'text-gray-500'}`} data-testid={`stat-subtitle-${index}`}>
                {stat.subtitle}
              </p>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

client/src/components/enrollment/Enrollm.content:
import React, { useState } from 'react';
import { Button } from '../ui/button';
import { Input } from '../ui/input';
import { Label } from '../ui/label';
import { Textarea } from '../ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '../ui/select';
import { Card, CardContent } from '../ui/card';
import { useToast } from '../../hooks/use-toast';
import { apiRequest } from '../../lib/queryClient';
// Firebase enrollment replaced with PostgreSQL placeholder
import { EnrollmentApplication } from '@shared/schema';

interface EnrollmentPortalProps {
  onBackToLogin: () => void;
}

export const EnrollmentPortal: React.FC<EnrollmentPortalProps> = ({ onBackToLogin }) => {
  const { toast } = useToast();
  const [currentStep, setCurrentStep] = useState(1);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phoneNumber: '',
    dateOfBirth: '',
    gender: '',
    address: '',
    parentName: '',
    parentPhone: '',
    parentEmail: '',
    desiredGradeLevel: '',
    desiredStrand: '',
    previousSchool: '',
    previousGPA: ''
  });

  const steps = [
    { step: 1, title: 'Personal Info', completed: currentStep > 1 },
    { step: 2, title: 'Academic Info', completed: currentStep > 2 },
    { step: 3, title: 'Documents', completed: currentStep > 3 },
    { step: 4, title: 'Payment', completed: currentStep > 4 },
    { step: 5, title: 'Review', completed: false },
  ];

  const handleChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleNext = () => {
    if (currentStep < 5) {
      setCurrentStep(currentStep + 1);
    }
  };

  const handlePrevious = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleSubmit = async () => {
    setLoading(true);
    try {
      // Create enrollment application
      const response = await fetch('/api/enrollment/applications', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          schoolYear: '2024-2025',
          studentInfo: {
            firstName: formData.firstName,
            lastName: formData.lastName,
            birthDate: formData.dateOfBirth,
            address: formData.address,
            parentContact: formData.parentPhone,
            phoneNumber: formData.phoneNumber,
            email: formData.email
          }
        })
      });

      const applicationResponse = await response.json();

      if (applicationResponse.id) {
        // Submit the application immediately for review
        await fetch(`/api/enrollment/applications/${applicationResponse.id}/submit`, {
          method: 'PATCH',
          headers: {
            'Content-Type': 'application/json'
          }
        });

        toast({
          title: "Enrollment Complete!",
          description: applicationResponse.message || "Your login credentials have been sent via SMS. You can now log in to access your student portal.",
        });

        // Show credentials in console for debugging
        if (applicationResponse.credentials) {
          console.log("Generated Login Credentials:", applicationResponse.credentials);
        }

        onBackToLogin();
      } else {
        throw new Error('Failed to create application');
      }
    } catch (error) {
      console.error('Error submitting application:', error);
      toast({
        title: "Submission failed",
        description: "Please try again later or contact the registrar office.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const renderProgressBar = () => (
    <div className="mb-8">
      <div className="flex items-center justify-between mb-4">
        {steps.map((step, index) => (
          <React.Fragment key={step.step}>
            <div className="flex items-center space-x-2">
              <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${
                step.step === currentStep 
                  ? 'bg-primary-600 text-white' 
                  : step.completed
                  ? 'bg-green-600 text-white'
                  : 'bg-gray-300 text-gray-600'
              }`}>
                {step.completed ? <i className="fas fa-check"></i> : step.step}
              </div>
              <span className={`${
                step.step === currentStep ? 'text-primary-600 font-medium' : 'text-gray-600'
              }`}>
                {step.title}
              </span>
            </div>
            {index < steps.length - 1 && (
              <div className={`w-12 h-0.5 ${step.completed ? 'bg-green-600' : 'bg-gray-300'}`}></div>
            )}
          </React.Fragment>
        ))}
      </div>
      <div className="w-full bg-gray-300 rounded-full h-2">
        <div 
          className="bg-primary-600 h-2 rounded-full transition-all duration-300" 
          style={{ width: `${(currentStep / 5) * 100}%` }}
        ></div>
      </div>
    </div>
  );

  const renderStepContent = () => {
    switch (currentStep) {
      case 1:
        return (
          <div className="space-y-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Personal Information</h2>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <Label htmlFor="firstName">First Name *</Label>
                <Input
                  id="firstName"
                  value={formData.firstName}
                  onChange={(e) => handleChange('firstName', e.target.value)}
                  placeholder="Enter first name"
                  required
                  data-testid="input-firstName"
                />
              </div>
              <div>
                <Label htmlFor="lastName">Last Name *</Label>
                <Input
                  id="lastName"
                  value={formData.lastName}
                  onChange={(e) => handleChange('lastName', e.target.value)}
                  placeholder="Enter last name"
                  required
                  data-testid="input-lastName"
                />
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <Label htmlFor="dateOfBirth">Date of Birth *</Label>
                <Input
                  id="dateOfBirth"
                  type="date"
                  value={formData.dateOfBirth}
                  onChange={(e) => handleChange('dateOfBirth', e.target.value)}
                  required
                  data-testid="input-dateOfBirth"
                />
              </div>
              <div>
                <Label htmlFor="gender">Gender *</Label>
                <Select value={formData.gender} onValueChange={(value) => handleChange('gender', value)}>
                  <SelectTrigger data-testid="select-gender">
                    <SelectValue placeholder="Select gender" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="male">Male</SelectItem>
                    <SelectItem value="female">Female</SelectItem>
                    <SelectItem value="other">Other</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div>
              <Label htmlFor="email">Email Address *</Label>
              <Input
                id="email"
                type="email"
                value={formData.email}
                onChange={(e) => handleChange('email', e.target.value)}
                placeholder="Enter email address"
                required
                data-testid="input-email"
              />
            </div>

            <div>
              <Label htmlFor="phoneNumber">Phone Number *</Label>
              <Input
                id="phoneNumber"
                type="tel"
                value={formData.phoneNumber}
                onChange={(e) => handleChange('phoneNumber', e.target.value)}
                placeholder="Enter phone number"
                required
                data-testid="input-phoneNumber"
              />
            </div>

            <div>
              <Label htmlFor="address">Complete Address *</Label>
              <Textarea
                id="address"
                value={formData.address}
                onChange={(e) => handleChange('address', e.target.value)}
                placeholder="Enter complete address"
                rows={3}
                required
                data-testid="input-address"
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <Label htmlFor="parentName">Parent/Guardian Name *</Label>
                <Input
                  id="parentName"
                  value={formData.parentName}
                  onChange={(e) => handleChange('parentName', e.target.value)}
                  placeholder="Enter parent/guardian name"
                  required
                  data-testid="input-parentName"
                />
              </div>
              <div>
                <Label htmlFor="parentPhone">Parent/Guardian Phone *</Label>
                <Input
                  id="parentPhone"
                  type="tel"
                  value={formData.parentPhone}
                  onChange={(e) => handleChange('parentPhone', e.target.value)}
                  placeholder="Enter parent/guardian phone"
                  required
                  data-testid="input-parentPhone"
                />
              </div>
            </div>
          </div>
        );

      case 2:
        return (
          <div className="space-y-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Academic Information</h2>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <Label htmlFor="desiredGradeLevel">Desired Grade Level *</Label>
                <Select value={formData.desiredGradeLevel} onValueChange={(value) => handleChange('desiredGradeLevel', value)}>
                  <SelectTrigger data-testid="select-gradeLevel">
                    <SelectValue placeholder="Select grade level" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="grade7">Grade 7</SelectItem>
                    <SelectItem value="grade8">Grade 8</SelectItem>
                    <SelectItem value="grade9">Grade 9</SelectItem>
                    <SelectItem value="grade10">Grade 10</SelectItem>
                    <SelectItem value="grade11">Grade 11</SelectItem>
                    <SelectItem value="grade12">Grade 12</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div>
                <Label htmlFor="desiredStrand">Desired Strand (for SHS)</Label>
                <Select value={formData.desiredStrand} onValueChange={(value) => handleChange('desiredStrand', value)}>
                  <SelectTrigger data-testid="select-strand">
                    <SelectValue placeholder="Select strand (if applicable)" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="stem">STEM</SelectItem>
                    <SelectItem value="abm">ABM</SelectItem>
                    <SelectItem value="humss">HUMSS</SelectItem>
                    <SelectItem value="gas">GAS</SelectItem>
                    <SelectItem value="tvl">TVL</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div>
              <Label htmlFor="previousSchool">Previous School</Label>
              <Input
                id="previousSchool"
                value={formData.previousSchool}
                onChange={(e) => handleChange('previousSchool', e.target.value)}
                placeholder="Enter previous school name"
                data-testid="input-previousSchool"
              />
            </div>

            <div>
              <Label htmlFor="previousGPA">Previous GPA (if applicable)</Label>
              <Input
                id="previousGPA"
                type="number"
                step="0.01"
                min="0"
                max="4.0"
                value={formData.previousGPA}
                onChange={(e) => handleChange('previousGPA', e.target.value)}
                placeholder="Enter previous GPA"
                data-testid="input-previousGPA"
              />
            </div>
          </div>
        );

      case 3:
        return (
          <div className="space-y-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Required Documents</h2>
            <div className="text-center py-8 border-2 border-dashed border-gray-300 rounded-lg">
              <i className="fas fa-upload text-4xl text-gray-400 mb-4"></i>
              <p className="text-gray-600">Document upload feature coming soon</p>
              <p className="text-sm text-gray-500 mt-2">Please prepare: Birth Certificate, Report Card, Valid ID</p>
            </div>
          </div>
        );

      case 4:
        return (
          <div className="space-y-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Payment Information</h2>
            <div className="text-center py-8 border border-gray-200 rounded-lg bg-gray-50">
              <i className="fas fa-credit-card text-4xl text-gray-400 mb-4"></i>
              <p className="text-gray-600">Payment processing will be handled by the accounting department</p>
              <p className="text-sm text-gray-500 mt-2">You will receive payment instructions via email</p>
            </div>
          </div>
        );

      case 5:
        return (
          <div className="space-y-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Review Application</h2>
            <div className="bg-gray-50 p-6 rounded-lg">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <span className="font-medium text-gray-600">Name:</span>
                  <p className="text-gray-900">{formData.firstName} {formData.lastName}</p>
                </div>
                <div>
                  <span className="font-medium text-gray-600">Email:</span>
                  <p className="text-gray-900">{formData.email}</p>
                </div>
                <div>
                  <span className="font-medium text-gray-600">Grade Level:</span>
                  <p className="text-gray-900">{formData.desiredGradeLevel}</p>
                </div>
                <div>
                  <span className="font-medium text-gray-600">Parent/Guardian:</span>
                  <p className="text-gray-900">{formData.parentName}</p>
                </div>
              </div>
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="absolute inset-0 bg-cover bg-center opacity-5" 
           style={{backgroundImage: "url('https://images.unsplash.com/photo-1562774053-701939374585?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&h=1080')"}} />

      <div className="relative z-10">
        {/* Header */}
        <header className="bg-white shadow-sm">
          <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <div className="w-10 h-10 bg-primary-600 rounded-xl flex items-center justify-center">
                  <i className="fas fa-graduation-cap text-white text-lg"></i>
                </div>
                <div>
                  <h1 className="text-2xl font-bold text-gray-900">Student Enrollment Portal</h1>
                  <p className="text-gray-600">Apply for admission to our school</p>
                </div>
              </div>
              <Button
                variant="ghost"
                onClick={onBackToLogin}
                className="text-gray-600 hover:text-primary-600"
                data-testid="button-close"
              >
                <i className="fas fa-times text-xl"></i>
              </Button>
            </div>
          </div>
        </header>

        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {renderProgressBar()}

          {/* Form */}
          <Card className="shadow-sm">
            <CardContent className="p-8">
              {renderStepContent()}

              {/* Navigation Buttons */}
              <div className="flex justify-between pt-6 mt-6 border-t border-gray-200">
                <Button 
                  variant="outline" 
                  onClick={currentStep === 1 ? onBackToLogin : handlePrevious}
                  data-testid="button-previous"
                >
                  {currentStep === 1 ? 'Cancel' : 'Previous'}
                </Button>

                {currentStep < 5 ? (
                  <Button 
                    onClick={handleNext}
                    className="bg-primary-600 hover:bg-primary-700"
                    data-testid="button-next"
                  >
                    Continue
                  </Button>
                ) : (
                  <Button 
                    onClick={handleSubmit}
                    className="bg-primary-600 hover:bg-primary-700"
                    disabled={loading}
                    data-testid="button-submit"
                  >
                    {loading ? 'Submitting...' : 'Submit Application'}
                  </Button>
                )}
              </div>
            </CardContent>
          </Card>

          {/* Status Tracking */}
          <Card className="mt-8 border-l-4 border-blue-500">
            <CardContent className="p-6">
              <h3 className="font-semibold text-gray-900 mb-2">Already Applied?</h3>
              <p className="text-gray-600 mb-4">Track your application status using your application ID.</p>
              <div className="flex space-x-3">
                <Input 
                  placeholder="Enter Application ID" 
                  className="flex-1"
                  data-testid="input-trackingId"
                />
                <Button 
                  variant="secondary"
                  className="bg-secondary-600 hover:bg-secondary-700 text-white"
                  data-testid="button-track"
                >
                  Track Status
                </Button>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
};

client/src/components/grades/GradesTable.content:
import React, { useState } from 'react';
import { Card, CardContent } from '../ui/card';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '../ui/select';
import { useRealtimeQuery } from '../../hooks/useRealtimeData';
import { useAuth } from '../../contexts/AuthContext';
import { Grade } from '@shared/schema';

export const GradesTable: React.FC = () => {
  const { userProfile } = useAuth();
  const [selectedQuarter, setSelectedQuarter] = useState('current');

  const { data: grades, loading } = useRealtimeQuery<Grade>('grades');

  if (loading) {
    return (
      <div className="flex items-center justify-center py-8">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  const getGradeColor = (grade: number) => {
    if (grade >= 90) return 'bg-green-100 text-green-800';
    if (grade >= 85) return 'bg-blue-100 text-blue-800';
    if (grade >= 80) return 'bg-yellow-100 text-yellow-800';
    return 'bg-red-100 text-red-800';
  };

  const calculateGPA = () => {
    if (grades.length === 0) return '0.00';
    const average = grades.reduce((sum, grade) => sum + grade.grade, 0) / grades.length;
    return (average / 25).toFixed(2); // Convert to 4.0 scale
  };

  const getCompletedSubjects = () => {
    return grades.filter(g => g.grade > 0).length;
  };

  const getAverageGrade = () => {
    if (grades.length === 0) return '0.0';
    const average = grades.reduce((sum, grade) => sum + grade.grade, 0) / grades.length;
    return average.toFixed(1);
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="text-2xl font-bold text-gray-900">My Grades</h2>
        <Select value={selectedQuarter} onValueChange={setSelectedQuarter}>
          <SelectTrigger className="w-48" data-testid="select-quarter">
            <SelectValue />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="current">Current Quarter</SelectItem>
            <SelectItem value="Q1">Q1 2024</SelectItem>
            <SelectItem value="Q2">Q2 2024</SelectItem>
            <SelectItem value="Q3">Q3 2024</SelectItem>
            <SelectItem value="Q4">Q4 2024</SelectItem>
          </SelectContent>
        </Select>
      </div>

      {/* Grades Overview */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <Card>
          <CardContent className="p-6 text-center">
            <div className="text-3xl font-bold text-primary-600 mb-2" data-testid="current-gpa">
              {calculateGPA()}
            </div>
            <p className="text-gray-600 font-medium">Current GPA</p>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-6 text-center">
            <div className="text-3xl font-bold text-green-600 mb-2" data-testid="completed-subjects">
              {getCompletedSubjects()}/{grades.length}
            </div>
            <p className="text-gray-600 font-medium">Subjects Graded</p>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-6 text-center">
            <div className="text-3xl font-bold text-orange-600 mb-2" data-testid="average-grade">
              {getAverageGrade()}
            </div>
            <p className="text-gray-600 font-medium">Average Grade</p>
          </CardContent>
        </Card>
      </div>

      {/* Subjects Grades Table */}
      <Card>
        <CardContent className="p-0">
          <div className="p-6 border-b border-gray-200">
            <h3 className="text-lg font-semibold text-gray-900">Subject Grades</h3>
          </div>
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Subject
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Teacher
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Q1
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Q2
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Q3
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Q4
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Final
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {grades.length === 0 ? (
                  <tr>
                    <td colSpan={7} className="px-6 py-8 text-center text-gray-500">
                      No grades available yet.
                    </td>
                  </tr>
                ) : (
                  grades.map((grade) => (
                    <tr key={grade.id} className="hover:bg-gray-50" data-testid={`grade-row-${grade.id}`}>
                      <td className="px-6 py-4">
                        <div className="font-medium text-gray-900">Subject Name</div>
                        <div className="text-sm text-gray-500">Course Description</div>
                      </td>
                      <td className="px-6 py-4 text-sm text-gray-900">
                        Teacher Name
                      </td>
                      <td className="px-6 py-4">
                        {grade.quarter === 'Q1' ? (
                          <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getGradeColor(grade.grade)}`}>
                            {grade.grade}
                          </span>
                        ) : (
                          <span className="text-gray-400">-</span>
                        )}
                      </td>
                      <td className="px-6 py-4">
                        {grade.quarter === 'Q2' ? (
                          <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getGradeColor(grade.grade)}`}>
                            {grade.grade}
                          </span>
                        ) : (
                          <span className="text-gray-400">-</span>
                        )}
                      </td>
                      <td className="px-6 py-4">
                        {grade.quarter === 'Q3' ? (
                          <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getGradeColor(grade.grade)}`}>
                            {grade.grade}
                          </span>
                        ) : (
                          <span className="text-gray-400">-</span>
                        )}
                      </td>
                      <td className="px-6 py-4">
                        {grade.quarter === 'Q4' ? (
                          <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getGradeColor(grade.grade)}`}>
                            {grade.grade}
                          </span>
                        ) : (
                          <span className="text-gray-400">-</span>
                        )}
                      </td>
                      <td className="px-6 py-4">
                        <span className="text-gray-400">-</span>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

client/src/components/layout/MainLayout..content:
import React, { useState, useEffect } from 'react';
import { Header } from '../navigation/Header';
import { Sidebar } from '../navigation/Sidebar';

import { Dashboard } from '../../pages/Dashboard';
import { Grades } from '../../pages/Grades';
import { Assignments } from '../../pages/Assignments';
import { Modules } from '../../pages/Modules';
import { Meetings } from '../../pages/Meetings';
import { Announcements } from '../../pages/Announcements';
import { TeacherDashboard } from '../../pages/teacher/TeacherDashboard';
import { EnhancedTeacherDashboard } from '../../pages/teacher/EnhancedTeacherDashboard';
import { AdminDashboard } from '../../pages/admin/AdminDashboard';
import { StudentDashboard } from '../../pages/student/StudentDashboard';
import { EnhancedStudentDashboard } from '../../pages/student/EnhancedStudentDashboard';
import { ParentDashboard } from '../../pages/parent/ParentDashboard';
import { GuidanceDashboard } from '../../pages/guidance/GuidanceDashboard';
import { EnhancedGuidanceDashboard } from '../../pages/guidance/EnhancedGuidanceDashboard';
import { RegistrarDashboard } from '../../pages/registrar/RegistrarDashboard';
import { EnhancedRegistrarDashboard } from '../../pages/registrar/EnhancedRegistrarDashboard';
import { AccountingDashboard } from '../../pages/accounting/AccountingDashboard';
import { EnhancedAccountingDashboard } from '../../pages/accounting/EnhancedAccountingDashboard';
import { PrincipalDashboard } from '../../pages/principal/PrincipalDashboard';
import { EnhancedAcademicDashboard } from '../../pages/academic/EnhancedAcademicDashboard';
import { StudentPaymentPage } from '../../pages/student/StudentPaymentPage';
import { FacebookStyleChat } from '../chat/FacebookStyleChat';
import { AdminControlPanel } from '../admin/AdminControlSimple';
import { UserManagement } from '../admin/UserManagement';
import FolderManagement from '../../pages/teacher/FolderManagement';
import SharedFolders from '../../pages/student/SharedFolders';
import { useAuth } from '../../contexts/AuthContext';

// Import admin page components
import { UserManagement as AdminUserManagement } from '../../pages/admin/UserManagement';
import { EnrollmentManagement } from '../../pages/admin/EnrollmentManagement';
import { AcademicSetup } from '../../pages/admin/AcademicSetup';
import { ContentManagement } from '../../pages/admin/ContentManagement';
import { MonitoringReports } from '../../pages/admin/MonitoringReports';
import { CommunicationTools } from '../../pages/admin/CommunicationTools';
import { SystemConfiguration } from '../../pages/admin/SystemConfiguration';

export const MainLayout: React.FC = () => {
  const { user } = useAuth();
  const [currentSection, setCurrentSection] = useState('dashboard');
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  // Handle responsive behavior
  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth < 1024);
      if (window.innerWidth >= 1024) {
        setIsSidebarOpen(false); // Auto-close mobile sidebar on desktop
      }
    };

    checkMobile();
    window.addEventListener('resize', checkMobile);
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  if (!user) return null;

  const renderContent = () => {
    // Role-specific dashboard routing
    if (currentSection === 'dashboard') {
      switch (user.role) {
        case 'admin':
          return <AdminDashboard onNavigate={setCurrentSection} />;
        case 'principal':
          return <PrincipalDashboard />;
        case 'academic_coordinator':
          return <EnhancedAcademicDashboard />;
        case 'teacher':
          return <EnhancedTeacherDashboard />;
        case 'student':
          return <EnhancedStudentDashboard />;
        case 'parent':
          return <ParentDashboard />;
        case 'guidance':
          return <EnhancedGuidanceDashboard />;
        case 'registrar':
          return <EnhancedRegistrarDashboard />;
        case 'accounting':
          return <EnhancedAccountingDashboard />;
        default:
          return <Dashboard />;
      }
    }

    // Common sections based on current selection
    switch (currentSection) {
      case 'grades':
        return <Grades />;
      case 'assignments':
        return <Assignments />;
      case 'modules':
        return <Modules />;
      case 'meetings':
        return <Meetings />;
      case 'announcements':
        return <Announcements />;
      case 'payments':
        // Student payments portal
        return user.role === 'student' ? <StudentPaymentPage /> : <Dashboard />;
      // Role-specific sections can be added here
      case 'classes':
        return <TeacherDashboard />; // For now, redirect to teacher dashboard
      case 'users':
      case 'admin-users':
        return <AdminUserManagement />;
      case 'admin-enrollment':
        return user.role === 'admin' ? <EnrollmentManagement /> : <Dashboard />;
      case 'admin-academic':
        return user.role === 'admin' ? <AcademicSetup /> : <Dashboard />;
      case 'admin-content':
        return user.role === 'admin' ? <ContentManagement /> : <Dashboard />;
      case 'admin-reports':
        return user.role === 'admin' ? <MonitoringReports /> : <Dashboard />;
      case 'admin-communication':
        return user.role === 'admin' ? <CommunicationTools /> : <Dashboard />;
      case 'admin-settings':
        return user.role === 'admin' ? <SystemConfiguration /> : <Dashboard />;
      case 'admin-control':
        return <AdminControlPanel />;
      case 'folders':
        return user.role === 'teacher' ? <FolderManagement /> : <Dashboard />;
      case 'shared-folders':
        return user.role === 'student' ? <SharedFolders /> : <Dashboard />;
      default:
        return <Dashboard />;
    }
  };

  return (
    <div className="flex h-screen w-full overflow-hidden bg-gradient-to-br from-purple-50 via-white to-indigo-50">
      {/* Mobile sidebar overlay */}
      {isSidebarOpen && (
        <div 
          className="fixed inset-0 z-40 bg-black/20 backdrop-blur-sm lg:hidden"
          onClick={() => setIsSidebarOpen(false)}
        />
      )}

      {/* Sidebar - No borders, full height */}
      <div className={`
        ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'} 
        fixed lg:relative lg:translate-x-0 z-50 lg:z-auto
        w-64 h-full bg-gradient-to-b from-gray-900 via-gray-800 to-gray-900
        transition-transform duration-300 ease-in-out
      `}>
        <Sidebar 
          currentSection={currentSection} 
          onSectionChange={(section) => {
            setCurrentSection(section);
            if (isMobile) setIsSidebarOpen(false);
          }}
        />
      </div>

      {/* Main content area - No borders, full space utilization */}
      <div className="flex-1 flex flex-col min-w-0 h-full">
        {/* Header - No borders */}
        <Header 
          onMenuClick={() => setIsSidebarOpen(!isSidebarOpen)}
          isMobile={isMobile}
        />

        {/* Content area - No borders, full height */}
        <main className="flex-1 overflow-auto h-full">
          <div className="w-full h-full">
            {renderContent()}
          </div>
        </main>
      </div>

      {/* Facebook Style Chat */}
      <FacebookStyleChat />
    </div>
  );
};

client/src/components/modules/ModuleCard.content:
import React from 'react';
import { Card, CardContent } from '../ui/card';
import { Button } from '../ui/button';
import { LearningModule } from '@shared/schema';
import { formatFileSize, getFileIcon } from '../../utils/firebaseHelpers';

interface ModuleCardProps {
  module: LearningModule;
  onDownload?: (moduleId: string) => void;
  onView?: (moduleId: string) => void;
}

export const ModuleCard: React.FC<ModuleCardProps> = ({
  module,
  onDownload,
  onView
}) => {
  const getFileTypeColor = () => {
    if (module.fileType.includes('pdf')) return 'text-red-600 bg-red-100';
    if (module.fileType.includes('word')) return 'text-blue-600 bg-blue-100';
    if (module.fileType.includes('video')) return 'text-purple-600 bg-purple-100';
    if (module.fileType.includes('image')) return 'text-green-600 bg-green-100';
    return 'text-gray-600 bg-gray-100';
  };

  const getFileTypeLabel = () => {
    if (module.fileType.includes('pdf')) return 'PDF';
    if (module.fileType.includes('word')) return 'DOC';
    if (module.fileType.includes('video')) return 'Video';
    if (module.fileType.includes('image')) return 'Image';
    return 'File';
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  return (
    <Card className="hover-lift border border-gray-200" data-testid={`module-card-${module.id}`}>
      <CardContent className="p-6">
        <div className="flex items-center justify-between mb-4">
          <div className={`w-12 h-12 rounded-lg flex items-center justify-center ${getFileTypeColor()}`}>
            <i className={`${getFileIcon(module.fileType)} text-xl`}></i>
          </div>
          <span className={`px-2 py-1 text-xs font-medium rounded-full ${getFileTypeColor()}`}>
            {getFileTypeLabel()}
          </span>
        </div>

        <h3 className="font-semibold text-gray-900 mb-2" data-testid={`module-title-${module.id}`}>
          {module.title}
        </h3>
        <p className="text-sm text-gray-600 mb-4 line-clamp-2" data-testid={`module-description-${module.id}`}>
          {module.description}
        </p>

        <div className="space-y-2 mb-4">
          <div className="flex items-center justify-between text-sm">
            <span className="text-gray-500">Subject:</span>
            <span className="font-medium">Subject Name</span>
          </div>
          <div className="flex items-center justify-between text-sm">
            <span className="text-gray-500">Teacher:</span>
            <span className="font-medium">Teacher Name</span>
          </div>
          <div className="flex items-center justify-between text-sm">
            <span className="text-gray-500">Size:</span>
            <span className="font-medium">{formatFileSize(module.fileSize)}</span>
          </div>
          <div className="flex items-center justify-between text-sm">
            <span className="text-gray-500">Uploaded:</span>
            <span className="font-medium">{formatDate(module.createdAt)}</span>
          </div>
          <div className="flex items-center justify-between text-sm">
            <span className="text-gray-500">Downloads:</span>
            <span className="font-medium">{module.downloadCount}</span>
          </div>
        </div>

        <div className="flex space-x-2">
          {onDownload && (
            <Button 
              className="flex-1 bg-primary-600 hover:bg-primary-700"
              onClick={() => onDownload(module.id)}
              data-testid={`button-download-${module.id}`}
            >
              <i className="fas fa-download mr-2"></i>Download
            </Button>
          )}
          {onView && (
            <Button
              variant="outline"
              className="px-4"
              onClick={() => onView(module.id)}
              data-testid={`button-view-${module.id}`}
            >
              <i className="fas fa-eye"></i>
            </Button>
          )}
        </div>
      </CardContent>
    </Card>
  );
};

