            </Button>
            <Button 
              variant="outline" 
              size="sm" 
              className="border-purple-200 text-purple-700 hover:bg-purple-50"
              onClick={() => onNavigate?.('admin-settings')}
            >
              <Settings className="h-4 w-4 mr-2" />
