              <button 
                onClick={() => onNavigate?.('admin-content')}
                className="px-4 py-2 text-sm font-medium text-gray-700 hover:bg-gray-100 border-r"
                data-testid="nav-content"
              >
                Content
              </button>
              <button 
                onClick={() => onNavigate?.('admin-reports')}
                className="px-4 py-2 text-sm font-medium text-gray-700 hover:bg-gray-100 border-r"
                data-testid="nav-reports"
              >
                Reports
