    resolver: zodResolver(z.object({
      teacherId: z.string(),
      sectionId: z.string(),
      subjectId: z.string(),
      dayOfWeek: z.string(),
      startTime: z.string(),
      endTime: z.string(),
      room: z.string()
    }))
  });

  const handleCreateSchedule = (data: any) => {
    createScheduleMutation.mutate(data);
