);

// Update module details (Teacher only)
router.patch('/:id', 
  requireAuth, 
  requireRole(['Teacher']), 
  async (req, res) => {
    try {
      const moduleId = parseInt(req.params.id);
      const teacherId = (req as any).user.id;
      const { title, description } = req.body;

