import { Router } from 'express';
import { z } from 'zod';
import { requireAuth, requireRole } from '../auth/jwt';
import { upload, validateFile, getFileUrl } from '../middleware/upload';
import { storage } from '../unified-storage';

const router = Router();

// Schema for enrollment application
const createApplicationSchema = z.object({
  schoolYear: z.string(),
  studentInfo: z.object({
    firstName: z.string().min(1),
    lastName: z.string().min(1),
    birthDate: z.string(),
    address: z.string().min(1),
    parentContact: z.string().min(1)
  })
});

// Create enrollment application (Student/Parent)
router.post('/applications', 
  requireAuth, 
  requireRole(['Student', 'Parent']), 
  async (req, res) => {
    try {
      const { schoolYear, studentInfo } = createApplicationSchema.parse(req.body);
      const userId = (req as any).user.id;

      // Create application
      const application = await storage.createEnrollmentApplication({
        studentId: userId,
        schoolYear,
        status: 'Draft',
        createdAt: new Date()
      });

      // Update enrollment progress
      await storage.updateEnrollmentProgress(userId, {
        applicationId: application.id,
        currentStatus: 'Draft',
        remarks: 'Application created'
      });

      res.status(201).json({
        id: application.id,
        message: 'Enrollment application created successfully'
      });

    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ 
          error: 'Invalid input', 
          details: error.errors 
        });
      }
      console.error('Create enrollment error:', error);
      res.status(500).json({ error: 'Failed to create application' });
    }
  }
);

// Upload enrollment documents
router.post('/applications/:id/documents', 
  requireAuth, 
  requireRole(['Student', 'Parent']), 
  upload.array('documents', 5),
  async (req, res) => {
    try {
      const applicationId = parseInt(req.params.id);
      const userId = (req as any).user.id;
      const { docType } = req.body;

      // Verify application ownership
      const application = await storage.getEnrollmentApplication(applicationId);
      if (!application || application.studentId !== userId) {
        return res.status(404).json({ error: 'Application not found or access denied' });
      }

      const files = req.files as Express.Multer.File[];
      if (!files || files.length === 0) {
        return res.status(400).json({ error: 'No documents uploaded' });
      }

      // Process each document
      const documents = [];
      for (const file of files) {
        const fileError = validateFile(file, {
          required: true,
          allowedTypes: ['application/pdf', 'image/jpeg', 'image/png'],
          maxSize: 10 * 1024 * 1024 // 10MB
        });

        if (fileError) {
          return res.status(400).json({ error: fileError });
        }

        const document = await storage.createEnrollmentDocument({
          applicationId,
          docType: docType || 'General',
          fileUrl: getFileUrl(file.path),
          uploadedAt: new Date()
        });

        documents.push(document);
      }

      // Update progress
      await storage.updateEnrollmentProgress(userId, {
        applicationId,
        currentStatus: 'Pending Documents',
        remarks: `${documents.length} document(s) uploaded`
      });

      res.json({
        message: 'Documents uploaded successfully',
        documents
      });

    } catch (error) {
      console.error('Upload documents error:', error);
      res.status(500).json({ error: 'Failed to upload documents' });
    }
  }
);

// Submit application for review
router.patch('/applications/:id/submit', 
  requireAuth, 
  requireRole(['Student', 'Parent']), 
  async (req, res) => {
    try {
      const applicationId = parseInt(req.params.id);
      const userId = (req as any).user.id;

      // Verify application ownership
      const application = await storage.getEnrollmentApplication(applicationId);
      if (!application || application.studentId !== userId) {
        return res.status(404).json({ error: 'Application not found or access denied' });
      }

      // Update application status
      await storage.updateEnrollmentApplication(applicationId, {
        status: 'Submitted',
        submittedAt: new Date()
      });

      // Update progress
      await storage.updateEnrollmentProgress(userId, {
        applicationId,
        currentStatus: 'Submitted',
        remarks: 'Application submitted for review'
      });

      // Notify registrar
      const registrars = await storage.getUsersByRole('Registrar');
      for (const registrar of registrars) {
        await storage.createNotification({
          userId: registrar.id,
          title: 'New Enrollment Application',
          body: `A new enrollment application has been submitted for review`,
          type: 'enrollment_submitted',
          relatedId: applicationId
        });
      }

      res.json({ message: 'Application submitted successfully' });

    } catch (error) {
      console.error('Submit application error:', error);
      res.status(500).json({ error: 'Failed to submit application' });
    }
  }
);

// Get enrollment progress (Student/Parent)
router.get('/progress/me', 
  requireAuth, 
  requireRole(['Student', 'Parent']), 
  async (req, res) => {
    try {
      const userId = (req as any).user.id;
      const progress = await storage.getEnrollmentProgress(userId);

      res.json(progress || { status: 'No application found' });

    } catch (error) {
      console.error('Get enrollment progress error:', error);
      res.status(500).json({ error: 'Failed to fetch progress' });
    }
  }
);

// Review enrollment applications (Registrar/Admin)
router.get('/applications', 
  requireAuth, 
  requireRole(['Registrar', 'Admin']), 
  async (req, res) => {
    try {
      const { status, page = 1, limit = 20 } = req.query;
      const applications = await storage.getEnrollmentApplications({
        status: status as string,
        page: parseInt(page as string),
        limit: parseInt(limit as string)
      });

      res.json(applications);

    } catch (error) {
      console.error('Get applications error:', error);
      res.status(500).json({ error: 'Failed to fetch applications' });
    }
  }
);

// Approve/Reject application (Registrar/Admin)
router.patch('/applications/:id/decision', 
  requireAuth, 
  requireRole(['Registrar', 'Admin']), 
  async (req, res) => {
    try {
      const applicationId = parseInt(req.params.id);
      const { decision, remarks } = req.body;
      const reviewerId = (req as any).user.id;

      if (!['Approved', 'Rejected'].includes(decision)) {
        return res.status(400).json({ error: 'Invalid decision. Must be Approved or Rejected' });
      }

      // Update application
      await storage.updateEnrollmentApplication(applicationId, {
        status: decision,
        decidedAt: new Date(),
        decidedBy: reviewerId,
        remarks
      });

      // Get application to notify student
      const application = await storage.getEnrollmentApplication(applicationId);
      if (application) {
        // Update progress
        await storage.updateEnrollmentProgress(application.studentId, {
          applicationId,
          currentStatus: decision,
          remarks: remarks || `Application ${decision.toLowerCase()}`
        });

        // Notify student
        await storage.createNotification({
          userId: application.studentId,
          title: `Enrollment Application ${decision}`,
          body: `Your enrollment application has been ${decision.toLowerCase()}${remarks ? ': ' + remarks : ''}`,
          type: 'enrollment_decision',
          relatedId: applicationId
        });
      }

      res.json({ message: `Application ${decision.toLowerCase()} successfully` });

    } catch (error) {
      console.error('Application decision error:', error);
      res.status(500).json({ error: 'Failed to process decision' });
    }
  }
);

export default router;

server/routes/enhanced-tasks.ts.content:
import { Router } from 'express';
import { z } from 'zod';
import { requireAuth, requireRole } from '../auth/jwt';
import { upload, validateFile, getFileUrl } from '../middleware/upload';
import { storage } from '../unified-storage';

const router = Router();

// Schema for task creation
const createTaskSchema = z.object({
  sectionId: z.number(),
  subjectId: z.number().optional(),
  title: z.string().min(1).max(255),
  description: z.string().optional(),
  type: z.enum(['Assignment', 'Quiz', 'Test']),
  timerMinutes: z.number().optional(),
  dueAt: z.string().datetime().optional(),
  questions: z.array(z.object({
    question: z.string(),
    choices: z.array(z.string()).optional(),
    answer: z.string()
  })).optional()
});

// Create task with questions (Teacher)
router.post('/', 
  requireAuth, 
  requireRole(['Teacher']), 
  async (req, res) => {
    try {
      const taskData = createTaskSchema.parse(req.body);
      const teacherId = (req as any).user.id;

      // Verify teacher has access to section
      const hasAccess = await storage.verifyTeacherSectionAccess(teacherId, taskData.sectionId);
      if (!hasAccess) {
        return res.status(403).json({ error: 'Access denied to this section' });
      }

      // Create task
      const task = await storage.createTask({
        teacherId,
        sectionId: taskData.sectionId,
        subjectId: taskData.subjectId,
        title: taskData.title,
        description: taskData.description || '',
        type: taskData.type,
        timerMinutes: taskData.timerMinutes,
        dueAt: taskData.dueAt ? new Date(taskData.dueAt) : null,
        createdAt: new Date()
      });

      // Add questions if provided
      if (taskData.questions && taskData.questions.length > 0) {
        for (const question of taskData.questions) {
          await storage.createTaskQuestion({
            taskId: task.id,
            question: question.question,
            choices: question.choices || null,
            answer: question.answer
          });
        }
      }

      // Notify students in section
      const students = await storage.getStudentsBySection(taskData.sectionId);
      for (const student of students) {
        await storage.createNotification({
          userId: student.id,
          title: `New ${taskData.type}: ${taskData.title}`,
          body: `A new ${taskData.type.toLowerCase()} has been assigned to your section`,
          type: 'task_assigned',
          relatedId: task.id
        });
      }

      res.status(201).json({
        id: task.id,
        message: 'Task created successfully',
        questionsCount: taskData.questions?.length || 0
      });

    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ 
          error: 'Invalid input', 
          details: error.errors 
        });
      }
      console.error('Create task error:', error);
      res.status(500).json({ error: 'Failed to create task' });
    }
  }
);

// Get tasks for section (Students/Teachers)
router.get('/section/:sectionId', requireAuth, async (req, res) => {
  try {
    const sectionId = parseInt(req.params.sectionId);
    const user = (req as any).user;

    // Verify access to section
    const hasAccess = await storage.verifyUserSectionAccess(user.id, sectionId, user.role);
    if (!hasAccess) {
      return res.status(403).json({ error: 'Access denied to this section' });
    }

    const tasks = await storage.getTasksBySection(sectionId);

    // If student, include submission status
    if (user.role === 'Student') {
      for (const task of tasks) {
        task.submission = await storage.getTaskSubmission(task.id, user.id);
      }
    }

    res.json(tasks);

  } catch (error) {
    console.error('Get section tasks error:', error);
    res.status(500).json({ error: 'Failed to fetch tasks' });
  }
});

// Get task with questions (for taking quiz/test)
router.get('/:id/questions', 
  requireAuth, 
  requireRole(['Student']), 
  async (req, res) => {
    try {
      const taskId = parseInt(req.params.id);
      const studentId = (req as any).user.id;

      // Get task
      const task = await storage.getTaskById(taskId);
      if (!task) {
        return res.status(404).json({ error: 'Task not found' });
      }

      // Verify student has access to this task's section
      const hasAccess = await storage.verifyUserSectionAccess(studentId, task.sectionId, 'Student');
      if (!hasAccess) {
        return res.status(403).json({ error: 'Access denied to this task' });
      }

      // Check if already submitted
      const existingSubmission = await storage.getTaskSubmission(taskId, studentId);
      if (existingSubmission) {
        return res.status(400).json({ error: 'Task already submitted' });
      }

      // Get questions (hide answers from students)
      const questions = await storage.getTaskQuestions(taskId);
      const studentQuestions = questions.map(q => ({
        id: q.id,
        question: q.question,
        choices: q.choices
        // Answer is hidden from students
      }));

      res.json({
        task: {
          id: task.id,
          title: task.title,
          description: task.description,
          type: task.type,
          timerMinutes: task.timerMinutes,
          dueAt: task.dueAt
        },
        questions: studentQuestions
      });

    } catch (error) {
      console.error('Get task questions error:', error);
      res.status(500).json({ error: 'Failed to fetch task questions' });
    }
  }
);

// Submit task (Student)
router.post('/:id/submissions', 
  requireAuth, 
  requireRole(['Student']), 
  upload.single('file'),
  async (req, res) => {
    try {
      const taskId = parseInt(req.params.id);
      const studentId = (req as any).user.id;
      const { submissionText, answers } = req.body;

      // Get task
      const task = await storage.getTaskById(taskId);
      if (!task) {
        return res.status(404).json({ error: 'Task not found' });
      }

      // Check if already submitted
      const existingSubmission = await storage.getTaskSubmission(taskId, studentId);
      if (existingSubmission) {
        return res.status(400).json({ error: 'Task already submitted' });
      }

      // Check due date
      if (task.dueAt && new Date() > new Date(task.dueAt)) {
        return res.status(400).json({ error: 'Task submission deadline has passed' });
      }

      let fileUrl = null;
      if (req.file) {
        const fileError = validateFile(req.file, {
          allowedTypes: ['application/pdf', 'application/msword', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document', 'text/plain'],
          maxSize: 25 * 1024 * 1024 // 25MB
        });

        if (fileError) {
          return res.status(400).json({ error: fileError });
        }

        fileUrl = getFileUrl(req.file.path);
      }

      // Auto-grade if it's a quiz/test with questions
      let score = null;
      if ((task.type === 'Quiz' || task.type === 'Test') && answers) {
        const questions = await storage.getTaskQuestions(taskId);
        const studentAnswers = JSON.parse(answers);
        let correctCount = 0;

        for (const question of questions) {
          const studentAnswer = studentAnswers[question.id];
          if (studentAnswer === question.answer) {
            correctCount++;
          }
        }

        score = (correctCount / questions.length) * 100;
      }

      // Create submission
      const submission = await storage.createTaskSubmission({
        taskId,
        studentId,
        submissionText: submissionText || '',
        fileUrl,
        submittedAt: new Date(),
        score,
        gradedAt: score !== null ? new Date() : null,
        gradedBy: score !== null ? null : null // Auto-graded
      });

      // Notify teacher
      await storage.createNotification({
        userId: task.teacherId,
        title: 'New Task Submission',
        body: `A student has submitted ${task.title}`,
        type: 'task_submitted',
        relatedId: taskId
      });

      res.status(201).json({
        id: submission.id,
        message: 'Task submitted successfully',
        score: score !== null ? Math.round(score) : null,
        autoGraded: score !== null
      });

    } catch (error) {
      console.error('Submit task error:', error);
      res.status(500).json({ error: 'Failed to submit task' });
    }
  }
);

// Grade submission (Teacher)
router.patch('/submissions/:id/grade', 
  requireAuth, 
  requireRole(['Teacher']), 
  async (req, res) => {
    try {
      const submissionId = parseInt(req.params.id);
      const { score, feedback } = req.body;
      const teacherId = (req as any).user.id;

      // Validate score
      if (score < 0 || score > 100) {
        return res.status(400).json({ error: 'Score must be between 0 and 100' });
      }

      // Get submission and verify teacher access
      const submission = await storage.getTaskSubmissionById(submissionId);
      if (!submission) {
        return res.status(404).json({ error: 'Submission not found' });
      }

      const task = await storage.getTaskById(submission.taskId);
      if (task.teacherId !== teacherId) {
        return res.status(403).json({ error: 'Access denied' });
      }

      // Update grade
      await storage.updateTaskSubmission(submissionId, {
        score,
        feedback,
        gradedAt: new Date(),
        gradedBy: teacherId
      });

      // Notify student
      await storage.createNotification({
        userId: submission.studentId,
        title: 'Task Graded',
        body: `Your submission for "${task.title}" has been graded: ${Math.round(score)}%`,
        type: 'task_graded',
        relatedId: submission.taskId
      });

      res.json({ message: 'Task graded successfully' });

    } catch (error) {
      console.error('Grade task error:', error);
      res.status(500).json({ error: 'Failed to grade task' });
    }
  }
);

export default router;

