import React, { useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/contexts/AuthContext";
import { useToast } from "@/hooks/use-toast";
import { Button } from '../ui/button';
import { ScrollArea } from '../ui/scroll-area';
import { Badge } from '../ui/badge';
import { formatDistanceToNow } from 'date-fns';
import { Bell, Check, X, Trash2, ExternalLink } from 'lucide-react';

interface Notification {
  id: number;
  recipientId: number;
  message: string;
  link?: string;
  isRead: boolean;
  createdAt: string;
  type?: 'info' | 'success' | 'warning' | 'error';
}

interface NotificationPanelProps {
  isOpen: boolean;
  onClose: () => void;
}

export const NotificationPanel: React.FC<NotificationPanelProps> = ({ isOpen, onClose }) => {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch notifications for current user
  // Temporarily disable notifications due to schema issues
  const notifications: any[] = [];
  const isLoading = false;

  // Mark notification as read
  const markAsReadMutation = useMutation({
    mutationFn: (notificationId: number) => 
      apiRequest(`/api/notifications/${notificationId}/read`, "PUT"),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/notifications"] });
    },
    onError: (error) => {
      console.error("Error marking notification as read:", error);
      toast({
        title: "Error",
        description: "Failed to mark notification as read",
        variant: "destructive",
      });
    },
  });

  // Mark all notifications as read
  const markAllAsReadMutation = useMutation({
    mutationFn: () => 
      apiRequest(`/api/notifications/mark-all-read`, "PUT", { recipientId: user?.id }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/notifications"] });
      toast({
        title: "Success",
        description: "All notifications marked as read",
      });
    },
    onError: (error) => {
      console.error("Error marking all notifications as read:", error);
      toast({
        title: "Error",
        description: "Failed to mark all notifications as read",
        variant: "destructive",
      });
    },
  });

  // Delete notification
  const deleteNotificationMutation = useMutation({
    mutationFn: (notificationId: number) => 
      apiRequest(`/api/notifications/${notificationId}`, "DELETE"),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/notifications"] });
      toast({
        title: "Success",
        description: "Notification deleted",
      });
    },
    onError: (error) => {
      console.error("Error deleting notification:", error);
      toast({
        title: "Error",
        description: "Failed to delete notification",
        variant: "destructive",
      });
    },
  });

  const handleNotificationClick = (notification: Notification) => {
    // Mark as read if not already read
    if (!notification.isRead) {
      markAsReadMutation.mutate(notification.id);
    }

    // Navigate to link if provided
    if (notification.link) {
      window.location.href = notification.link;
    }
  };

  const getNotificationIcon = (type?: string) => {
    switch (type) {
      case 'success':
        return 'âœ…';
      case 'warning':
        return 'âš ï¸';
      case 'error':
        return 'âŒ';
      default:
        return 'ðŸ“¢';
    }
  };

  const unreadCount = notifications.filter((n: Notification) => !n.isRead).length;

  if (!isOpen) return null;

  return (
    <div className="fixed right-0 top-16 w-96 h-96 bg-white shadow-2xl border-l border-gray-200 z-50 flex flex-col">
      {/* Header */}
      <div className="p-4 border-b border-gray-200 bg-primary text-primary-foreground">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <Bell className="h-5 w-5" />
            <h3 className="font-semibold" data-testid="notifications-title">
              Notifications
              {unreadCount > 0 && (
                <Badge variant="secondary" className="ml-2">
                  {unreadCount}
                </Badge>
              )}
            </h3>
          </div>
          <Button
            variant="ghost"
            size="sm"
            className="text-primary-foreground hover:text-primary-foreground/80 p-1"
            onClick={onClose}
            data-testid="button-close-notifications"
          >
            <X className="h-4 w-4" />
          </Button>
        </div>
      </div>

      {/* Actions */}
      {unreadCount > 0 && (
        <div className="p-2 border-b border-gray-200">
          <Button
            variant="outline"
            size="sm"
            className="w-full"
            onClick={() => markAllAsReadMutation.mutate()}
            disabled={markAllAsReadMutation.isPending}
            data-testid="button-mark-all-read"
          >
            <Check className="h-4 w-4 mr-2" />
            Mark All as Read
          </Button>
        </div>
      )}

      {/* Notifications List */}
      <ScrollArea className="flex-1">
        <div className="p-2">
          {isLoading ? (
            <div className="text-center py-8">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto"></div>
              <p className="text-sm text-gray-500 mt-2">Loading notifications...</p>
            </div>
          ) : notifications.length === 0 ? (
            <div className="text-center py-8">
              <Bell className="h-12 w-12 text-gray-300 mx-auto mb-4" />
              <p className="text-sm text-gray-500">No notifications yet</p>
            </div>
          ) : (
            <div className="space-y-2">
              {notifications.map((notification: Notification) => (
                <div
                  key={notification.id}
                  className={`p-3 rounded-lg border cursor-pointer transition-colors ${
                    notification.isRead
                      ? 'bg-gray-50 border-gray-200'
                      : 'bg-blue-50 border-blue-200'
                  } hover:bg-gray-100`}
                  onClick={() => handleNotificationClick(notification)}
                  data-testid={`notification-${notification.id}`}
                >
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <div className="flex items-center space-x-2">
                        <span className="text-lg">
                          {getNotificationIcon(notification.type)}
                        </span>
                        <p className="text-sm font-medium text-gray-900">
                          {notification.message}
                        </p>
                        {!notification.isRead && (
                          <div className="w-2 h-2 bg-blue-500 rounded-full" />
                        )}
                      </div>
                      <div className="flex items-center justify-between mt-2">
                        <p className="text-xs text-gray-500">
                          {formatDistanceToNow(new Date(notification.createdAt), { addSuffix: true })}
                        </p>
                        {notification.link && (
                          <ExternalLink className="h-3 w-3 text-gray-400" />
                        )}
                      </div>
                    </div>
                    <div className="flex items-center space-x-1 ml-2">
                      {!notification.isRead && (
                        <Button
                          variant="ghost"
                          size="sm"
                          className="p-1 h-6 w-6"
                          onClick={(e) => {
                            e.stopPropagation();
                            markAsReadMutation.mutate(notification.id);
                          }}
                          disabled={markAsReadMutation.isPending}
                          data-testid={`button-mark-read-${notification.id}`}
                        >
                          <Check className="h-3 w-3" />
                        </Button>
                      )}
                      <Button
                        variant="ghost"
                        size="sm"
                        className="p-1 h-6 w-6 text-red-500 hover:text-red-700"
                        onClick={(e) => {
                          e.stopPropagation();
                          deleteNotificationMutation.mutate(notification.id);
                        }}
                        disabled={deleteNotificationMutation.isPending}
                        data-testid={`button-delete-${notification.id}`}
                      >
                        <Trash2 className="h-3 w-3" />
                      </Button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </ScrollArea>
    </div>
  );
};

client/src/components/ui/dashboard-backg.content:
import React, { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Slider } from '@/components/ui/slider';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Settings, Upload, X, Palette } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';

interface DashboardBackgroundProps {
  children: React.ReactNode;
  userRole: string;
  className?: string;
}

interface BackgroundSettings {
  imageUrl: string;
  opacity: number;
  enabled: boolean;
}

const defaultBackgrounds = [
  {
    name: 'School Campus',
    url: 'https://images.unsplash.com/photo-1523050854058-8df90110c9f1?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80'
  },
  {
    name: 'Library Books',
    url: 'https://images.unsplash.com/photo-1481627834876-b7833e8f5570?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80'
  },
  {
    name: 'Classroom',
    url: 'https://images.unsplash.com/photo-1580582932707-520aed937b7b?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80'
  },
  {
    name: 'Study Space',
    url: 'https://images.unsplash.com/photo-1513475382585-d06e58bcb0e0?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80'
  },
  {
    name: 'Academic',
    url: 'https://images.unsplash.com/photo-1503676260728-1c00da094a0b?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80'
  },
  {
    name: 'Modern Office',
    url: 'https://images.unsplash.com/photo-1497366216548-37526070297c?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80'
  }
];

export function DashboardBackground({ children, userRole, className = '' }: DashboardBackgroundProps) {
  const [settings, setSettings] = useState<BackgroundSettings>({
    imageUrl: '',
    opacity: 0.1,
    enabled: false
  });
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const { toast } = useToast();

  // Load saved settings from localStorage
  useEffect(() => {
    const savedSettings = localStorage.getItem(`dashboard-bg-${userRole}`);
    if (savedSettings) {
      try {
        const parsed = JSON.parse(savedSettings);
        setSettings(parsed);
      } catch (error) {
        console.error('Error parsing background settings:', error);
      }
    }
  }, [userRole]);

  // Save settings to localStorage
  const saveSettings = (newSettings: BackgroundSettings) => {
    setSettings(newSettings);
    localStorage.setItem(`dashboard-bg-${userRole}`, JSON.stringify(newSettings));
    toast({
      title: "Background Updated",
      description: "Your dashboard background settings have been saved.",
    });
  };

  const handleOpacityChange = (value: number[]) => {
    const newSettings = { ...settings, opacity: value[0] };
    saveSettings(newSettings);
  };

  const handleBackgroundSelect = (imageUrl: string) => {
    const newSettings = { ...settings, imageUrl, enabled: true };
    saveSettings(newSettings);
  };

  const handleToggleBackground = () => {
    const newSettings = { ...settings, enabled: !settings.enabled };
    saveSettings(newSettings);
  };

  const handleRemoveBackground = () => {
    const newSettings = { ...settings, enabled: false, imageUrl: '' };
    saveSettings(newSettings);
  };

  const backgroundStyle = settings.enabled && settings.imageUrl ? {
    backgroundImage: `url(${settings.imageUrl})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    backgroundAttachment: 'fixed',
    position: 'relative' as const,
    minHeight: '100vh',
    minWidth: '100vw',
  } : {};

  return (
    <div 
      className={`min-h-screen ${className}`}
      style={backgroundStyle}
      data-testid="dashboard-background"
    >
      {/* Background Overlay */}
      {settings.enabled && settings.imageUrl && (
        <div 
          className="absolute inset-0 bg-white dark:bg-gray-900"
          style={{ opacity: 1 - settings.opacity }}
        />
      )}

      {/* Content */}
      <div className="relative z-10">
        {/* Background Settings Button */}
        <div className="fixed top-20 right-4 z-50">
          <Dialog open={isSettingsOpen} onOpenChange={setIsSettingsOpen}>
            <DialogTrigger asChild>
              <Button
                size="sm"
                variant="outline"
                className="bg-white/90 dark:bg-gray-800/90 backdrop-blur-sm border-gray-200 dark:border-gray-700 hover:bg-white dark:hover:bg-gray-800"
                data-testid="background-settings-trigger"
              >
                <Palette className="h-4 w-4 mr-2" />
                Customize
              </Button>
            </DialogTrigger>
            <DialogContent className="sm:max-w-md">
              <DialogHeader>
                <DialogTitle>Dashboard Background</DialogTitle>
                <DialogDescription>
                  Customize your dashboard appearance with background images and opacity settings.
                </DialogDescription>
              </DialogHeader>

              <div className="space-y-6">
                {/* Background Toggle */}
                <div className="flex items-center justify-between">
                  <span className="text-sm font-medium">Enable Background</span>
                  <Button
                    size="sm"
                    variant={settings.enabled ? "default" : "outline"}
                    onClick={handleToggleBackground}
                    data-testid="background-toggle"
                  >
                    {settings.enabled ? 'Enabled' : 'Disabled'}
                  </Button>
                </div>

                {settings.enabled && (
                  <>
                    {/* Opacity Slider */}
                    <div className="space-y-3">
                      <div className="flex items-center justify-between">
                        <span className="text-sm font-medium">Background Opacity</span>
                        <span className="text-sm text-muted-foreground">
                          {Math.round(settings.opacity * 100)}%
                        </span>
                      </div>
                      <Slider
                        value={[settings.opacity]}
                        onValueChange={handleOpacityChange}
                        max={0.8}
                        min={0.05}
                        step={0.05}
                        className="w-full"
                        data-testid="opacity-slider"
                      />
                    </div>

                    {/* Background Selection */}
                    <div className="space-y-3">
                      <span className="text-sm font-medium">Select Background</span>
                      <div className="grid grid-cols-2 gap-2">
                        {defaultBackgrounds.map((bg, index) => (
                          <div
                            key={index}
                            className={`relative cursor-pointer rounded-lg overflow-hidden border-2 transition-all ${
                              settings.imageUrl === bg.url 
                                ? 'border-blue-500 ring-2 ring-blue-200' 
                                : 'border-gray-200 hover:border-gray-300'
                            }`}
                            onClick={() => handleBackgroundSelect(bg.url)}
                            data-testid={`background-option-${index}`}
                          >
                            <img
                              src={bg.url}
                              alt={bg.name}
                              className="w-full h-16 object-cover"
                            />
                            <div className="absolute inset-0 bg-black/20 flex items-center justify-center">
                              <span className="text-white text-xs font-medium text-center px-1">
                                {bg.name}
                              </span>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>

                    {/* Remove Background */}
                    {settings.imageUrl && (
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={handleRemoveBackground}
                        className="w-full"
                        data-testid="remove-background"
                      >
                        <X className="h-4 w-4 mr-2" />
                        Remove Background
                      </Button>
                    )}
                  </>
                )}
              </div>
            </DialogContent>
          </Dialog>
        </div>

        {children}
      </div>
    </div>
  );
}

client/src/components/admin/AdminControl.content:
import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Badge } from '@/components/ui/badge';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';
import { Settings, Palette, Upload, Calendar, FileImage, Users, School } from 'lucide-react';

interface SystemSettings {
  primaryColor: string;
  secondaryColor: string;
  accentColor: string;
  logoUrl: string;
  bannerImages: string[];
  organizationChartUrl: string;
  schoolYear: string;
  schoolName: string;
  schoolAddress: string;
  schoolMotto: string;
  principalMessage: string;
  visionStatement: string;
  missionStatement: string;
}

interface SchoolYear {
  id: string;
  year: string;
  startDate: string;
  endDate: string;
  isActive: boolean;
  createdAt: string;
}

export function AdminControlPanel() {
  const [activeTab, setActiveTab] = useState('appearance');
  const [newSchoolYear, setNewSchoolYear] = useState('');
  const [colorSettings, setColorSettings] = useState({
    primaryColor: '#3b82f6',
    secondaryColor: '#64748b',
    accentColor: '#10b981'
  });
  const [uploadingImage, setUploadingImage] = useState<string | null>(null);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch current system settings
  const { data: settings, isLoading: settingsLoading } = useQuery<SystemSettings>({
    queryKey: ['/api/admin/settings'],
    queryFn: () => apiRequest('/api/admin/settings')
  });

  // Fetch school years
  const { data: schoolYears = [], isLoading: yearsLoading } = useQuery<SchoolYear[]>({
    queryKey: ['/api/admin/school-years'],
    queryFn: () => apiRequest('/api/admin/school-years')
  });

  // Update system settings mutation
  const updateSettingsMutation = useMutation({
    mutationFn: (updatedSettings: Partial<SystemSettings>) =>
      apiRequest('/api/admin/settings', {
        method: 'PUT',
        body: JSON.stringify(updatedSettings)
      }),
    onSuccess: () => {
      toast({
        title: 'Settings Updated',
        description: 'System settings have been updated successfully.'
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/settings'] });
    }
  });

  // Create new school year mutation
  const createSchoolYearMutation = useMutation({
    mutationFn: (yearData: { year: string; startDate: string; endDate: string }) =>
      apiRequest('/api/admin/school-years', {
        method: 'POST',
        body: JSON.stringify(yearData)
      }),
    onSuccess: () => {
      toast({
        title: 'School Year Created',
        description: 'New school year has been created and activated. Student dashboards have been reset.',
        variant: 'default'
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/school-years'] });
      setNewSchoolYear('');
    }
  });

  // Image upload mutation
  const uploadImageMutation = useMutation({
    mutationFn: async ({ file, type }: { file: File; type: string }) => {
      const formData = new FormData();
      formData.append('file', file);
      formData.append('type', type);

      return apiRequest('/api/admin/upload-image', {
        method: 'POST',
        body: formData
      });
    },
    onSuccess: (data, variables) => {
      toast({
        title: 'Image Uploaded',
        description: `${variables.type} has been uploaded successfully.`
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/settings'] });
      setUploadingImage(null);
    }
  });

  const handleColorChange = (colorType: keyof typeof colorSettings, value: string) => {
    setColorSettings(prev => ({ ...prev, [colorType]: value }));
  };

  const handleSaveColors = () => {
    updateSettingsMutation.mutate(colorSettings);
  };

  const handleImageUpload = (event: React.ChangeEvent<HTMLInputElement>, type: string) => {
    const file = event.target.files?.[0];
    if (file) {
      setUploadingImage(type);
      uploadImageMutation.mutate({ file, type });
    }
  };

  const handleContentUpdate = (field: keyof SystemSettings, value: string) => {
    updateSettingsMutation.mutate({ [field]: value });
  };

  const handleCreateSchoolYear = () => {
    if (!newSchoolYear) return;

    const currentYear = new Date().getFullYear();
    const nextYear = currentYear + 1;

    createSchoolYearMutation.mutate({
      year: newSchoolYear,
      startDate: `${currentYear}-08-01`,
      endDate: `${nextYear}-07-31`
    });
  };

  if (settingsLoading || yearsLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto"></div>
          <p className="mt-2 text-sm text-muted-foreground">Loading admin controls...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center gap-2">
        <Settings className="h-6 w-6" />
        <h1 className="text-2xl font-bold">Admin Control Panel</h1>
      </div>

      <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
        <TabsList className="grid w-full grid-cols-5">
          <TabsTrigger value="appearance" className="flex items-center gap-2">
            <Palette className="h-4 w-4" />
            Appearance
          </TabsTrigger>
          <TabsTrigger value="content" className="flex items-center gap-2">
            <FileImage className="h-4 w-4" />
            Content
          </TabsTrigger>
          <TabsTrigger value="images" className="flex items-center gap-2">
            <Upload className="h-4 w-4" />
            Images
          </TabsTrigger>
          <TabsTrigger value="organization" className="flex items-center gap-2">
            <Users className="h-4 w-4" />
            Organization
          </TabsTrigger>
          <TabsTrigger value="school-year" className="flex items-center gap-2">
            <Calendar className="h-4 w-4" />
            School Year
          </TabsTrigger>
        </TabsList>

        {/* Appearance Settings */}
        <TabsContent value="appearance" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Palette className="h-5 w-5" />
                Color Theme Settings
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div>
                  <Label htmlFor="primary-color">Primary Color</Label>
                  <div className="flex items-center gap-2 mt-1">
                    <Input
                      id="primary-color"
                      type="color"
                      value={colorSettings.primaryColor}
                      onChange={(e) => handleColorChange('primaryColor', e.target.value)}
                      className="w-16 h-10 p-0 border-0"
                    />
                    <Input
                      value={colorSettings.primaryColor}
                      onChange={(e) => handleColorChange('primaryColor', e.target.value)}
                      placeholder="#3b82f6"
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="secondary-color">Secondary Color</Label>
                  <div className="flex items-center gap-2 mt-1">
                    <Input
                      id="secondary-color"
                      type="color"
                      value={colorSettings.secondaryColor}
                      onChange={(e) => handleColorChange('secondaryColor', e.target.value)}
                      className="w-16 h-10 p-0 border-0"
                    />
                    <Input
                      value={colorSettings.secondaryColor}
                      onChange={(e) => handleColorChange('secondaryColor', e.target.value)}
                      placeholder="#64748b"
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="accent-color">Accent Color</Label>
                  <div className="flex items-center gap-2 mt-1">
                    <Input
                      id="accent-color"
                      type="color"
                      value={colorSettings.accentColor}
                      onChange={(e) => handleColorChange('accentColor', e.target.value)}
                      className="w-16 h-10 p-0 border-0"
                    />
                    <Input
                      value={colorSettings.accentColor}
                      onChange={(e) => handleColorChange('accentColor', e.target.value)}
                      placeholder="#10b981"
                    />
                  </div>
                </div>
              </div>

              <Button onClick={handleSaveColors} disabled={updateSettingsMutation.isPending}>
                {updateSettingsMutation.isPending ? 'Saving...' : 'Save Color Settings'}
              </Button>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Content Management */}
        <TabsContent value="content" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <School className="h-5 w-5" />
                School Information
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label htmlFor="school-name">School Name</Label>
                <Input
                  id="school-name"
                  defaultValue={settings?.schoolName || ''}
                  onBlur={(e) => handleContentUpdate('schoolName', e.target.value)}
                />
              </div>

              <div>
                <Label htmlFor="school-address">School Address</Label>
                <Textarea
                  id="school-address"
                  defaultValue={settings?.schoolAddress || ''}
                  onBlur={(e) => handleContentUpdate('schoolAddress', e.target.value)}
                />
              </div>

              <div>
                <Label htmlFor="school-motto">School Motto</Label>
                <Input
                  id="school-motto"
                  defaultValue={settings?.schoolMotto || ''}
                  onBlur={(e) => handleContentUpdate('schoolMotto', e.target.value)}
                />
              </div>

              <div>
                <Label htmlFor="principal-message">Principal's Message</Label>
                <Textarea
                  id="principal-message"
                  defaultValue={settings?.principalMessage || ''}
                  onBlur={(e) => handleContentUpdate('principalMessage', e.target.value)}
                  rows={4}
                />
              </div>

              <div>
                <Label htmlFor="vision-statement">Vision Statement</Label>
                <Textarea
                  id="vision-statement"
                  defaultValue={settings?.visionStatement || ''}
                  onBlur={(e) => handleContentUpdate('visionStatement', e.target.value)}
                  rows={3}
                />
              </div>

              <div>
                <Label htmlFor="mission-statement">Mission Statement</Label>
                <Textarea
                  id="mission-statement"
                  defaultValue={settings?.missionStatement || ''}
                  onBlur={(e) => handleContentUpdate('missionStatement', e.target.value)}
                  rows={3}
                />
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Image Management */}
        <TabsContent value="images" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Upload System Images</CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <Label htmlFor="logo-upload">School Logo</Label>
                  <div className="mt-2">
                    {settings?.logoUrl && (
                      <img 
                        src={settings.logoUrl} 
                        alt="School Logo" 
                        className="w-24 h-24 object-contain mb-2 border rounded"
                      />
                    )}
                    <Input
                      id="logo-upload"
                      type="file"
                      accept="image/*"
                      onChange={(e) => handleImageUpload(e, 'logo')}
                      disabled={uploadingImage === 'logo'}
                    />
                    {uploadingImage === 'logo' && (
                      <p className="text-sm text-muted-foreground mt-1">Uploading logo...</p>
                    )}
                  </div>
                </div>

                <div>
                  <Label htmlFor="banner-upload">Sliding Banner Images</Label>
                  <div className="mt-2">
                    {settings?.bannerImages && settings.bannerImages.length > 0 && (
                      <div className="grid grid-cols-3 gap-2 mb-2">
                        {settings.bannerImages.slice(0, 6).map((image, index) => (
                          <img
                            key={index}
                            src={image}
                            alt={`Banner ${index + 1}`}
                            className="w-full h-16 object-cover rounded border"
                          />
                        ))}
                      </div>
                    )}
                    <Input
                      id="banner-upload"
                      type="file"
                      accept="image/*"
                      multiple
                      onChange={(e) => handleImageUpload(e, 'banner')}
