import React, { useState, useEffect, useRef } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { useAuth } from '@/contexts/AuthContext';
import { useChat } from '@/contexts/ChatContext';
import { useToast } from '@/hooks/use-toast';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { io, Socket } from 'socket.io-client';
import {
  Card,
  CardContent,
  CardHeader,
} from '@/components/ui/card';
import {
  ScrollArea,
} from '@/components/ui/scroll-area';
import {
  Form,
  FormControl,
  FormField,
  FormItem,
} from '@/components/ui/form';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { Avatar, AvatarFallback } from '@/components/ui/avatar';
import { Separator } from '@/components/ui/separator';
import {
  Send,
  Users,
  MessageSquare,
  Circle,
  Search,
  Phone,
  Video,
  MoreVertical,
  Paperclip,
  Smile,
  X,
  ArrowLeft,
  Minimize2,
  MessageCircle,
} from 'lucide-react';
import type { User, Message, UserStatus } from '@shared/schema';

// Form schema for sending messages
const messageFormSchema = z.object({
  messageText: z.string().min(1, 'Message cannot be empty'),
});

type MessageFormData = z.infer<typeof messageFormSchema>;

interface ConversationWithDetails {
  id: string;
  conversationType: 'private' | 'group';
  partnerId?: number;
  partnerName?: string;
  partnerRole?: string;
  lastMessage?: string;
  lastMessageTime?: string;
  unreadCount?: number;
  createdAt: string;
}

interface OnlineUser {
  userId: number;
  user?: User;
  isOnline: boolean;
  lastSeen?: string;
}

export function FacebookStyleChat() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [socket, setSocket] = useState<Socket | null>(null);
  const [selectedConversation, setSelectedConversation] = useState<ConversationWithDetails | null>(null);
  const [messageText, setMessageText] = useState('');
  const [isMinimized, setIsMinimized] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const typingTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  // Form for sending messages
  const messageForm = useForm<MessageFormData>({
    resolver: zodResolver(messageFormSchema),
    defaultValues: {
      messageText: '',
    },
  });

  // Fetch conversations
  const { data: conversations = [] } = useQuery({
    queryKey: ['/api/chat/conversations'],
    queryFn: () => apiRequest(`/api/chat/conversations?userId=${user?.id}`),
    enabled: !!user,
    refetchInterval: 5000,
  });

  // Fetch all users for online status
  const { data: users = [] } = useQuery({
    queryKey: ['/api/users'],
    enabled: !!user,
  });

  // Fetch online users
  const { data: onlineUsers = [] } = useQuery<User[]>({
    queryKey: ['/api/chat/online-users'],
    refetchInterval: 10000,
    enabled: !!user,
  });

  // Fetch messages for selected conversation
  const { data: messages = [] } = useQuery({
    queryKey: ['/api/chat/messages', selectedConversation?.partnerId],
    queryFn: () => 
      selectedConversation?.partnerId
        ? apiRequest(`/api/chat/messages?userId=${user?.id}&partnerId=${selectedConversation.partnerId}`)
        : Promise.resolve([]),
    enabled: !!user && !!selectedConversation?.partnerId,
    refetchInterval: 2000,
  });

  // Send message mutation
  const sendMessageMutation = useMutation({
    mutationFn: async (messageData: { senderId: number; recipientId: number; messageText: string }) => {
      return apiRequest('/api/chat/messages', 'POST', messageData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/chat/messages'] });
      queryClient.invalidateQueries({ queryKey: ['/api/chat/conversations'] });
      setMessageText('');
      messageForm.reset();
    },
    onError: (error) => {
      console.error('Error sending message:', error);
      toast({
        title: 'Error',
        description: 'Failed to send message',
        variant: 'destructive',
      });
    },
  });

  // Initialize Socket.IO connection
  useEffect(() => {
    if (user) {
      const newSocket = io();
      setSocket(newSocket);

      newSocket.emit('join_user', user.id);

      newSocket.on('new_message', (message) => {
        queryClient.invalidateQueries({ queryKey: ['/api/chat/messages'] });
        queryClient.invalidateQueries({ queryKey: ['/api/chat/conversations'] });
      });

      newSocket.on('user_online', (data) => {
        queryClient.invalidateQueries({ queryKey: ['/api/chat/online-users'] });
      });

      newSocket.on('user_offline', (data) => {
        queryClient.invalidateQueries({ queryKey: ['/api/chat/online-users'] });
      });

      return () => {
        newSocket.disconnect();
      };
    }
  }, [user, queryClient]);

  // Auto-scroll to bottom when new messages arrive or conversation changes
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages, selectedConversation]);

  // Scroll to bottom when component mounts or conversation is selected
  useEffect(() => {
    const scrollToBottom = () => {
      if (messagesEndRef.current) {
        messagesEndRef.current.scrollIntoView({ behavior: 'auto' });
      }
    };

    // Scroll immediately and after a short delay to ensure content is rendered
    scrollToBottom();
    const timeoutId = setTimeout(scrollToBottom, 100);

    return () => clearTimeout(timeoutId);
  }, [selectedConversation]);

  const handleSendMessage = () => {
    if (!messageText.trim() || !selectedConversation || !user) return;

    const recipientId = selectedConversation.partnerId;

    if (!recipientId) {
      toast({
        title: 'Error',
        description: 'Cannot identify message recipient',
        variant: 'destructive',
      });
      return;
    }

    const messageData = {
      senderId: user.id,
      recipientId: recipientId,
      messageText: messageText.trim(),
    };

    sendMessageMutation.mutate(messageData);
    setMessageText('');

    // Scroll to bottom after sending message
    setTimeout(() => {
      if (messagesEndRef.current) {
        messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
      }
    }, 100);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const startConversation = (targetUser: User) => {
    // Check if conversation already exists
    const existingConversation = conversations.find((conv: ConversationWithDetails) => 
      conv.partnerId === targetUser.id
    );

    if (existingConversation) {
      setSelectedConversation(existingConversation);
    } else {
      // Create new conversation object
      const newConversation: ConversationWithDetails = {
        id: `conv_${Math.min(user?.id || 0, targetUser.id)}_${Math.max(user?.id || 0, targetUser.id)}`,
        conversationType: 'private',
        partnerId: targetUser.id,
        partnerName: targetUser.name,
        partnerRole: targetUser.role,
        lastMessage: '',
        unreadCount: 0,
        createdAt: new Date().toISOString(),
      };
      setSelectedConversation(newConversation);
    }
  };

  const isUserOnline = (userId: number) => {
    // Check if the API returns user objects directly (current format)
    if (onlineUsers.length > 0 && 'id' in onlineUsers[0]) {
      return onlineUsers.some((user: User) => user.id === userId);
    }
    // Fallback to status objects format
    return (onlineUsers as unknown as OnlineUser[]).some((status: OnlineUser) => status.userId === userId && status.isOnline);
  };

  const getRoleColor = (role: string) => {
    switch (role) {
      case 'admin': return 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200';
      case 'teacher': return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200';
      case 'student': return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200';
      case 'parent': return 'bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200';
      case 'guidance': return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200';
      case 'registrar': return 'bg-pink-100 text-pink-800 dark:bg-pink-900 dark:text-pink-200';
      case 'accounting': return 'bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200';
    }
  };

  const formatTime = (timestamp: string | Date) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diffInHours = (now.getTime() - date.getTime()) / (1000 * 60 * 60);

    if (diffInHours < 24) {
      return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    } else {
      return date.toLocaleDateString();
    }
  };

  // Filter online users based on search
  const filteredOnlineUsers = (() => {
    // If onlineUsers API returns user objects directly
    if (onlineUsers.length > 0 && 'id' in onlineUsers[0]) {
      return onlineUsers.filter((u: User) => 
        u.id !== user?.id && 
        u.name && u.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }
    // Otherwise, filter from all users and check online status
    return (users as User[]).filter((u: User) => 
      u.id !== user?.id && 
      isUserOnline(u.id) &&
      u.name && u.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
  })();

  // Filter conversations based on search
  const filteredConversations = conversations.filter((conv: ConversationWithDetails) =>
    conv.partnerName?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (!user) return null;

  if (isMinimized) {
    return (
      <div className="fixed bottom-4 right-4 z-50">
        <div className="relative">
          <Button
            onClick={() => setIsMinimized(false)}
            className="rounded-full h-14 w-14 bg-blue-600 hover:bg-blue-700 shadow-xl"
            data-testid="button-expand-chat"
          >
            <MessageCircle className="h-7 w-7 text-white" />
          </Button>
          {filteredOnlineUsers.length > 0 && (
            <div className="absolute -top-1 -right-1 bg-green-500 text-white text-xs rounded-full h-6 w-6 flex items-center justify-center font-bold">
              {filteredOnlineUsers.length}
            </div>
          )}
          {filteredOnlineUsers.length === 0 && onlineUsers.length > 0 && (
            <div className="absolute -top-1 -right-1 bg-green-500 text-white text-xs rounded-full h-6 w-6 flex items-center justify-center font-bold">
              {onlineUsers.length}
            </div>
          )}
          <div className="absolute bottom-0 right-1 bg-green-500 rounded-full h-4 w-4 border-2 border-white"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed bottom-0 right-4 w-96 h-[500px] bg-white dark:bg-gray-900 shadow-2xl border border-gray-200 dark:border-gray-700 rounded-t-lg z-50 flex flex-col">
      {/* Chat Header */}
      <div className="flex items-center justify-between p-3 bg-primary text-primary-foreground rounded-t-lg">
        <div className="flex items-center gap-2">
          {selectedConversation ? (
            <>
              <Button
                variant="ghost"
                size="sm"
                className="p-1 text-primary-foreground hover:text-primary-foreground/80"
                onClick={() => setSelectedConversation(null)}
                data-testid="button-back-chat-list"
              >
                <ArrowLeft className="h-4 w-4" />
              </Button>
              <span className="font-medium text-sm">{selectedConversation.partnerName}</span>
              {selectedConversation.partnerId && isUserOnline(selectedConversation.partnerId) && (
                <Circle className="h-2 w-2 fill-green-400 text-green-400" />
              )}
            </>
          ) : (
            <>
              <MessageSquare className="h-4 w-4" />
              <span className="font-medium text-sm">Chat</span>
            </>
          )}
        </div>
        <div className="flex items-center gap-1">
          {selectedConversation && (
            <>
              <Button
                variant="ghost"
                size="sm"
                className="p-1 text-primary-foreground hover:text-primary-foreground/80"
                data-testid="button-voice-call"
              >
                <Phone className="h-4 w-4" />
              </Button>
              <Button
                variant="ghost"
                size="sm"
                className="p-1 text-primary-foreground hover:text-primary-foreground/80"
                data-testid="button-video-call"
              >
                <Video className="h-4 w-4" />
              </Button>
            </>
          )}
          <Button
            variant="ghost"
            size="sm"
            className="p-1 text-primary-foreground hover:text-primary-foreground/80"
            onClick={() => setIsMinimized(true)}
            data-testid="button-minimize-chat"
          >
            <Minimize2 className="h-4 w-4" />
          </Button>
        </div>
      </div>

      {selectedConversation ? (
        // Chat View
        <div className="flex-1 flex flex-col">
          {/* Messages Area */}
          <ScrollArea className="flex-1 p-3 max-h-80 overflow-y-auto">
            <div className="space-y-2 min-h-full">
              {messages.length === 0 ? (
                <div className="flex items-center justify-center h-32 text-gray-500 dark:text-gray-400">
                  <div className="text-center">
                    <MessageSquare className="h-8 w-8 mx-auto mb-2 opacity-50" />
                    <p className="text-sm">No messages yet</p>
                    <p className="text-xs">Start the conversation!</p>
                  </div>
                </div>
              ) : (
                messages.map((message: Message) => (
                  <div
                    key={message.id}
                    className={`flex ${message.senderId === user.id ? 'justify-end' : 'justify-start'} mb-2`}
                  >
                    <div
                      className={`max-w-[70%] px-3 py-2 rounded-2xl word-wrap break-words ${
                        message.senderId === user.id
                          ? 'bg-primary text-primary-foreground rounded-br-md'
                          : 'bg-gray-100 dark:bg-gray-700 text-gray-900 dark:text-gray-100 rounded-bl-md'
                      }`}
                    >
                      <p className="text-sm leading-relaxed whitespace-pre-wrap">{message.messageText}</p>
                      <p className={`text-xs mt-1 ${
                        message.senderId === user.id 
                          ? 'text-primary-foreground/70' 
                          : 'text-gray-500 dark:text-gray-400'
                      }`}>
                        {formatTime(message.createdAt || new Date())}
                      </p>
                    </div>
                  </div>
                ))
              )}
              <div ref={messagesEndRef} className="h-1" />
            </div>
          </ScrollArea>

          {/* Message Input */}
          <div className="p-3 border-t border-gray-200 dark:border-gray-700 bg-white dark:bg-gray-900 sticky bottom-0">
            <div className="flex items-end gap-2">
              <Button
                variant="ghost"
                size="sm"
                className="p-1 mb-1"
                data-testid="button-attach-file"
              >
                <Paperclip className="h-4 w-4" />
              </Button>
              <div className="flex-1 relative">
                <Input
                  value={messageText}
                  onChange={(e) => setMessageText(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="Type a message..."
                  className="pr-16 min-h-[40px] resize-none rounded-full"
                  data-testid="input-message"
                  autoComplete="off"
                />
                <div className="absolute right-2 top-1/2 transform -translate-y-1/2 flex items-center gap-1">
                  <Button
                    variant="ghost"
                    size="sm"
                    className="p-1 h-6 w-6"
                    data-testid="button-emoji"
                  >
                    <Smile className="h-3 w-3" />
                  </Button>
                  <Button
                    onClick={handleSendMessage}
                    disabled={!messageText.trim() || sendMessageMutation.isPending}
                    size="sm"
                    className="p-1 h-6 w-6 rounded-full"
                    data-testid="button-send-message"
                  >
                    <Send className="h-3 w-3" />
                  </Button>
                </div>
              </div>
            </div>
          </div>
        </div>
      ) : (
        // Main Chat List View
        <div className="flex-1 flex">
          {/* Conversations List */}
          <div className="flex-1 flex flex-col border-r border-gray-200 dark:border-gray-700">
            {/* Search */}
            <div className="p-3 border-b border-gray-200 dark:border-gray-700">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                <Input
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  placeholder="Search..."
                  className="pl-9 text-sm"
                  data-testid="input-search"
                />
              </div>
            </div>

            {/* Conversations */}
            <ScrollArea className="flex-1">
              <div className="p-2">
                {filteredConversations.length === 0 ? (
                  <div className="text-center text-gray-500 dark:text-gray-400 py-4">
                    <MessageSquare className="h-8 w-8 mx-auto mb-2 opacity-50" />
                    <p className="text-xs">No conversations</p>
                  </div>
                ) : (
                  filteredConversations.map((conversation: ConversationWithDetails) => (
                    <div
                      key={conversation.id}
                      className="flex items-center gap-2 p-2 rounded-lg hover:bg-gray-50 dark:hover:bg-gray-800 cursor-pointer"
                      onClick={() => setSelectedConversation(conversation)}
                      data-testid={`conversation-${conversation.partnerId}`}
                    >
                      <div className="relative">
                        <Avatar className="h-8 w-8">
                          <AvatarFallback className="text-xs">
                            {conversation.partnerName?.charAt(0) || '?'}
                          </AvatarFallback>
                        </Avatar>
                        {conversation.partnerId && isUserOnline(conversation.partnerId) && (
                          <Circle className="absolute -bottom-0.5 -right-0.5 h-3 w-3 fill-green-500 text-green-500 border-2 border-white dark:border-gray-900" />
                        )}
                      </div>
                      <div className="flex-1 min-w-0">
                        <div className="flex items-center justify-between">
                          <p className="font-medium text-xs truncate">{conversation.partnerName}</p>
                          {conversation.unreadCount && conversation.unreadCount > 0 && (
                            <span className="bg-primary text-primary-foreground text-xs rounded-full px-1.5 py-0.5 min-w-[16px] text-center">
                              {conversation.unreadCount}
                            </span>
                          )}
                        </div>
                        <p className="text-xs text-gray-500 dark:text-gray-400 truncate">
                          {conversation.lastMessage || 'Start a conversation'}
                        </p>
                      </div>
                    </div>
                  ))
                )}
              </div>
            </ScrollArea>
          </div>

          {/* Online Users Sidebar */}
          <div className="w-24 flex flex-col bg-gray-50 dark:bg-gray-800">
            <div className="p-2 border-b border-gray-200 dark:border-gray-700">
              <div className="flex items-center justify-center">
                <Circle className="h-2 w-2 fill-green-500 text-green-500 mr-1" />
                <span className="text-xs font-medium">{filteredOnlineUsers.length}</span>
              </div>
            </div>

            <ScrollArea className="flex-1 h-[400px] max-h-[60vh]">
              <div className="p-1">
                {filteredOnlineUsers.map((onlineUser: User) => (
                  <div
                    key={onlineUser.id}
                    className="flex flex-col items-center p-1 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-700 cursor-pointer mb-2"
                    onClick={() => startConversation(onlineUser)}
                    data-testid={`online-user-${onlineUser.id}`}
                  >
                    <div className="relative">
                      <Avatar className="h-10 w-10">
                        <AvatarFallback className="text-xs">
                          {onlineUser.name.charAt(0)}
                        </AvatarFallback>
                      </Avatar>
                      <Circle className="absolute -bottom-0.5 -right-0.5 h-3 w-3 fill-green-500 text-green-500 border-2 border-white dark:border-gray-800" />
                    </div>
                    <span className="text-xs text-center truncate w-full mt-1" title={onlineUser.name}>
                      {onlineUser.name.split(' ')[0]}
                    </span>
                    <Badge variant="outline" className={`text-xs px-1 py-0 mt-1 ${getRoleColor(onlineUser.role)}`}>
                      {onlineUser.role}
                    </Badge>
                  </div>
                ))}

                {filteredOnlineUsers.length === 0 && (
                  <div className="text-center text-gray-500 dark:text-gray-400 py-4">
                    <Users className="h-6 w-6 mx-auto mb-1 opacity-50" />
                    <p className="text-xs">No users online</p>
                  </div>
                )}
              </div>
            </ScrollArea>
          </div>
        </div>
      )}
    </div>
  );
}

client/src/pages/student/SharedFolders.t.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Folder, FileText, User, Calendar, Eye, Download } from "lucide-react";

interface SharedFolder {
  id: number;
  name: string;
  description: string | null;
  teacherName: string;
  createdAt: string;
  documentsCount: number;
}

interface FolderDocument {
  id: number;
  folderId: number;
  name: string;
  fileUrl: string;
  fileType: string | null;
  fileSize: number | null;
  uploadedAt: string;
}

export default function SharedFolders() {
  const [selectedFolder, setSelectedFolder] = useState<SharedFolder | null>(null);

  // Fetch shared folders for the student
  const { data: sharedFolders = [], isLoading: foldersLoading } = useQuery<SharedFolder[]>({
    queryKey: ['/api/student/shared-folders']
  });

  // Fetch documents for selected folder
  const { data: documents = [] } = useQuery<FolderDocument[]>({
    queryKey: ['/api/teacher/folders', selectedFolder?.id, 'documents'],
    enabled: !!selectedFolder?.id
  });

  const formatFileSize = (bytes: number | null) => {
    if (!bytes) return 'Unknown size';

    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(1024));
    return Math.round((bytes / Math.pow(1024, i)) * 100) / 100 + ' ' + sizes[i];
  };

  const getFileIcon = (fileType: string | null) => {
    if (!fileType) return <FileText className="h-5 w-5 text-gray-600" />;

    if (fileType.includes('image')) return <FileText className="h-5 w-5 text-green-600" />;
    if (fileType.includes('pdf')) return <FileText className="h-5 w-5 text-red-600" />;
    if (fileType.includes('video')) return <FileText className="h-5 w-5 text-purple-600" />;
    if (fileType.includes('audio')) return <FileText className="h-5 w-5 text-orange-600" />;
    return <FileText className="h-5 w-5 text-blue-600" />;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-6">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Shared Learning Materials</h1>
          <p className="text-gray-600">Access learning materials shared by your teachers</p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Shared Folders List */}
          <div className="lg:col-span-1">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Folder className="h-5 w-5" />
                  Shared Folders
                </CardTitle>
              </CardHeader>
              <CardContent>
                {foldersLoading ? (
                  <div className="space-y-2">
                    {[1, 2, 3].map((i) => (
                      <div key={i} className="h-20 bg-gray-100 rounded-md animate-pulse" />
                    ))}
                  </div>
                ) : sharedFolders.length === 0 ? (
                  <div className="text-center text-gray-500 py-8">
                    <Folder className="h-12 w-12 mx-auto mb-2 opacity-50" />
                    <p>No shared folders</p>
                    <p className="text-sm">Your teachers haven't shared any materials yet</p>
                  </div>
                ) : (
                  <div className="space-y-2">
                    {sharedFolders.map((folder) => (
                      <div
                        key={folder.id}
                        className={`p-3 rounded-md border cursor-pointer transition-colors ${
                          selectedFolder?.id === folder.id
                            ? 'bg-blue-50 border-blue-200'
                            : 'bg-white border-gray-200 hover:bg-gray-50'
                        }`}
                        onClick={() => setSelectedFolder(folder)}
                        data-testid={`shared-folder-item-${folder.id}`}
                      >
                        <div className="flex items-start gap-2">
                          <Folder className="h-5 w-5 text-blue-600 mt-0.5 flex-shrink-0" />
                          <div className="flex-1 min-w-0">
                            <h3 className="font-medium text-sm truncate">{folder.name}</h3>
                            {folder.description && (
                              <p className="text-xs text-gray-500 line-clamp-2 mt-1">
                                {folder.description}
                              </p>
                            )}

                            <div className="flex items-center gap-2 mt-2">
                              <div className="flex items-center gap-1">
                                <User className="h-3 w-3 text-gray-400" />
                                <span className="text-xs text-gray-600">{folder.teacherName}</span>
                              </div>
                            </div>

                            <div className="flex items-center justify-between mt-2">
                              <div className="flex items-center gap-1">
                                <Calendar className="h-3 w-3 text-gray-400" />
                                <span className="text-xs text-gray-500">
                                  {new Date(folder.createdAt).toLocaleDateString()}
                                </span>
                              </div>
                              <Badge variant="secondary" className="text-xs">
                                {folder.documentsCount} files
                              </Badge>
                            </div>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>
          </div>

          {/* Folder Contents */}
          <div className="lg:col-span-2">
            {selectedFolder ? (
              <Card>
                <CardHeader>
                  <div className="flex justify-between items-start">
                    <div>
                      <CardTitle className="flex items-center gap-2">
                        <Folder className="h-5 w-5 text-blue-600" />
                        {selectedFolder.name}
                      </CardTitle>
                      {selectedFolder.description && (
                        <CardDescription className="mt-1">
                          {selectedFolder.description}
                        </CardDescription>
                      )}
                    </div>
                  </div>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="flex items-center gap-4 text-sm text-gray-600">
                      <div className="flex items-center gap-1">
                        <User className="h-4 w-4" />
                        Teacher: {selectedFolder.teacherName}
                      </div>
                      <div className="flex items-center gap-1">
                        <Calendar className="h-4 w-4" />
                        Shared: {new Date(selectedFolder.createdAt).toLocaleDateString()}
                      </div>
                      <div className="flex items-center gap-1">
                        <FileText className="h-4 w-4" />
                        {selectedFolder.documentsCount} documents
                      </div>
                    </div>

                    <div className="border-t pt-4">
                      <h3 className="font-medium mb-3">Learning Materials</h3>
                      {documents.length === 0 ? (
                        <div className="text-center text-gray-500 py-8">
                          <FileText className="h-12 w-12 mx-auto mb-2 opacity-50" />
                          <p>No documents available</p>
                          <p className="text-sm">This folder doesn't contain any materials yet</p>
                        </div>
                      ) : (
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                          {documents.map((document) => (
                            <Card 
                              key={document.id} 
                              className="hover:shadow-md transition-shadow"
                              data-testid={`document-card-${document.id}`}
                            >
                              <CardContent className="p-4">
                                <div className="flex items-start gap-3">
                                  {getFileIcon(document.fileType)}
                                  <div className="flex-1 min-w-0">
                                    <h4 className="font-medium text-sm line-clamp-2 mb-1">
                                      {document.name}
                                    </h4>
                                    <div className="space-y-1">
                                      {document.fileType && (
                                        <Badge variant="outline" className="text-xs">
                                          {document.fileType.split('/').pop()?.toUpperCase()}
                                        </Badge>
                                      )}
                                      <p className="text-xs text-gray-500">
                                        {formatFileSize(document.fileSize)}
                                      </p>
                                      <p className="text-xs text-gray-500">
                                        Uploaded {new Date(document.uploadedAt).toLocaleDateString()}
                                      </p>
                                    </div>
                                  </div>
                                </div>

                                <div className="flex gap-2 mt-3">
                                  <Button
                                    variant="outline"
                                    size="sm"
                                    className="flex-1"
                                    onClick={() => window.open(document.fileUrl, '_blank')}
                                    data-testid={`button-view-${document.id}`}
                                  >
                                    <Eye className="h-4 w-4 mr-1" />
                                    View
                                  </Button>
                                  <Button
                                    variant="default"
                                    size="sm"
                                    className="flex-1"
                                    onClick={() => {
                                      const link = document.createElement('a');
                                      link.href = document.fileUrl;
                                      link.download = document.name;
                                      link.target = '_blank';
                                      link.click();
                                    }}
                                    data-testid={`button-download-${document.id}`}
                                  >
                                    <Download className="h-4 w-4 mr-1" />
                                    Access
                                  </Button>
                                </div>
                              </CardContent>
                            </Card>
                          ))}
                        </div>
                      )}
                    </div>
                  </div>
                </CardContent>
              </Card>
            ) : (
              <Card>
                <CardContent className="flex flex-col items-center justify-center py-16">
                  <Folder className="h-16 w-16 text-gray-300 mb-4" />
                  <h3 className="text-lg font-medium text-gray-900 mb-2">No folder selected</h3>
                  <p className="text-gray-500 text-center">
                    Select a shared folder from the left to view the learning materials
                  </p>
                </CardContent>
              </Card>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/teacher/FolderManagemen.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { Checkbox } from "@/components/ui/checkbox";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Folder, FileText, Share2, Plus, Upload, Eye, Calendar, Users } from "lucide-react";

interface TeacherFolder {
  id: number;
  name: string;
  description: string | null;
  teacherId: number;
  createdAt: string;
}

interface FolderDocument {
  id: number;
  folderId: number;
  name: string;
  fileUrl: string;
  fileType: string | null;
  fileSize: number | null;
  uploadedAt: string;
}

interface Section {
  id: number;
  name: string;
  gradeLevel: string;
}

export default function FolderManagement() {
  const [selectedFolder, setSelectedFolder] = useState<TeacherFolder | null>(null);
  const [showCreateDialog, setShowCreateDialog] = useState(false);
  const [showShareDialog, setShowShareDialog] = useState(false);
  const [showUploadDialog, setShowUploadDialog] = useState(false);
  const [newFolderName, setNewFolderName] = useState("");
  const [newFolderDescription, setNewFolderDescription] = useState("");
  const [selectedSections, setSelectedSections] = useState<number[]>([]);
  const [newDocumentName, setNewDocumentName] = useState("");
  const [newDocumentUrl, setNewDocumentUrl] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch teacher folders
  const { data: folders = [], isLoading: foldersLoading } = useQuery<TeacherFolder[]>({
    queryKey: ['/api/teacher/folders']
  });

  // Fetch sections for sharing
  const { data: sections = [] } = useQuery<Section[]>({
    queryKey: ['/api/teacher/sections']
  });

  // Fetch documents for selected folder
  const { data: documents = [] } = useQuery<FolderDocument[]>({
    queryKey: ['/api/teacher/folders', selectedFolder?.id, 'documents'],
    enabled: !!selectedFolder?.id
  });

  // Create folder mutation
  const createFolderMutation = useMutation({
    mutationFn: async (data: { name: string; description: string }) => {
      return apiRequest('/api/teacher/folders', 'POST', data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/teacher/folders'] });
      setShowCreateDialog(false);
      setNewFolderName("");
      setNewFolderDescription("");
      toast({
        title: "Success",
        description: "Folder created successfully"
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create folder",
        variant: "destructive"
      });
    }
  });

  // Share folder mutation
  const shareFolderMutation = useMutation({
    mutationFn: async (data: { folderId: number; sectionIds: number[] }) => {
      return apiRequest(`/api/teacher/folders/${data.folderId}/share`, 'POST', { sectionIds: data.sectionIds });
    },
    onSuccess: () => {
      setShowShareDialog(false);
      setSelectedSections([]);
      toast({
        title: "Success",
        description: "Folder shared successfully"
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to share folder",
        variant: "destructive"
      });
    }
  });

  // Upload document mutation
  const uploadDocumentMutation = useMutation({
    mutationFn: async (data: { folderId: number; name: string; fileUrl: string }) => {
      return apiRequest(`/api/teacher/folders/${data.folderId}/documents`, 'POST', data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ 
        queryKey: ['/api/teacher/folders', selectedFolder?.id, 'documents'] 
      });
      setShowUploadDialog(false);
      setNewDocumentName("");
      setNewDocumentUrl("");
      toast({
        title: "Success",
        description: "Document uploaded successfully"
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to upload document",
        variant: "destructive"
      });
    }
  });

  const handleCreateFolder = () => {
    if (!newFolderName.trim()) return;
    createFolderMutation.mutate({
      name: newFolderName,
      description: newFolderDescription
    });
  };

  const handleShareFolder = () => {
    if (!selectedFolder || selectedSections.length === 0) return;
    shareFolderMutation.mutate({
      folderId: selectedFolder.id,
      sectionIds: selectedSections
    });
  };

  const handleUploadDocument = () => {
    if (!selectedFolder || !newDocumentName.trim() || !newDocumentUrl.trim()) return;
    uploadDocumentMutation.mutate({
      folderId: selectedFolder.id,
      name: newDocumentName,
      fileUrl: newDocumentUrl
    });
  };

  const handleSectionToggle = (sectionId: number) => {
    setSelectedSections(prev => 
      prev.includes(sectionId) 
        ? prev.filter(id => id !== sectionId)
        : [...prev, sectionId]
    );
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 to-emerald-100 p-6">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Learning Materials Manager</h1>
          <p className="text-gray-600">Create folders, upload documents, and share learning materials with your sections</p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Folders List */}
          <div className="lg:col-span-1">
            <Card>
              <CardHeader>
                <div className="flex justify-between items-center">
                  <CardTitle className="flex items-center gap-2">
                    <Folder className="h-5 w-5" />
                    My Folders
                  </CardTitle>
                  <Dialog open={showCreateDialog} onOpenChange={setShowCreateDialog}>
                    <DialogTrigger asChild>
                      <Button size="sm" data-testid="button-create-folder">
                        <Plus className="h-4 w-4 mr-1" />
                        New
                      </Button>
                    </DialogTrigger>
                    <DialogContent>
                      <DialogHeader>
                        <DialogTitle>Create New Folder</DialogTitle>
                        <DialogDescription>
                          Create a new folder to organize your learning materials
                        </DialogDescription>
                      </DialogHeader>
                      <div className="space-y-4">
                        <div>
                          <label className="text-sm font-medium">Folder Name</label>
                          <Input
                            value={newFolderName}
                            onChange={(e) => setNewFolderName(e.target.value)}
                            placeholder="Enter folder name"
                            data-testid="input-folder-name"
                          />
                        </div>
                        <div>
                          <label className="text-sm font-medium">Description (Optional)</label>
                          <Textarea
                            value={newFolderDescription}
                            onChange={(e) => setNewFolderDescription(e.target.value)}
                            placeholder="Enter folder description"
                            data-testid="input-folder-description"
                          />
                        </div>
                        <div className="flex justify-end gap-2">
                          <Button variant="outline" onClick={() => setShowCreateDialog(false)}>
                            Cancel
                          </Button>
                          <Button 
                            onClick={handleCreateFolder}
                            disabled={createFolderMutation.isPending}
                            data-testid="button-save-folder"
                          >
                            {createFolderMutation.isPending ? "Creating..." : "Create"}
                          </Button>
                        </div>
                      </div>
                    </DialogContent>
                  </Dialog>
                </div>
              </CardHeader>
              <CardContent>
                {foldersLoading ? (
                  <div className="space-y-2">
                    {[1, 2, 3].map((i) => (
                      <div key={i} className="h-16 bg-gray-100 rounded-md animate-pulse" />
                    ))}
                  </div>
                ) : folders.length === 0 ? (
                  <div className="text-center text-gray-500 py-8">
                    <Folder className="h-12 w-12 mx-auto mb-2 opacity-50" />
                    <p>No folders yet</p>
                    <p className="text-sm">Create your first folder to get started</p>
                  </div>
                ) : (
                  <div className="space-y-2">
                    {folders.map((folder) => (
                      <div
                        key={folder.id}
                        className={`p-3 rounded-md border cursor-pointer transition-colors ${
                          selectedFolder?.id === folder.id
                            ? 'bg-green-50 border-green-200'
                            : 'bg-white border-gray-200 hover:bg-gray-50'
                        }`}
                        onClick={() => setSelectedFolder(folder)}
                        data-testid={`folder-item-${folder.id}`}
                      >
                        <div className="flex items-start gap-2">
                          <Folder className="h-5 w-5 text-green-600 mt-0.5 flex-shrink-0" />
                          <div className="flex-1 min-w-0">
                            <h3 className="font-medium text-sm truncate">{folder.name}</h3>
                            {folder.description && (
                              <p className="text-xs text-gray-500 line-clamp-2 mt-1">
                                {folder.description}
                              </p>
                            )}
                            <div className="flex items-center gap-1 mt-2">
                              <Calendar className="h-3 w-3 text-gray-400" />
                              <span className="text-xs text-gray-500">
                                {new Date(folder.createdAt).toLocaleDateString()}
                              </span>
                            </div>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>
          </div>

          {/* Folder Details */}
          <div className="lg:col-span-2">
            {selectedFolder ? (
              <Card>
                <CardHeader>
                  <div className="flex justify-between items-start">
                    <div>
                      <CardTitle className="flex items-center gap-2">
                        <Folder className="h-5 w-5 text-green-600" />
                        {selectedFolder.name}
                      </CardTitle>
                      {selectedFolder.description && (
                        <CardDescription className="mt-1">
                          {selectedFolder.description}
                        </CardDescription>
                      )}
                    </div>
                    <div className="flex gap-2">
                      <Dialog open={showShareDialog} onOpenChange={setShowShareDialog}>
                        <DialogTrigger asChild>
                          <Button variant="outline" size="sm" data-testid="button-share-folder">
                            <Share2 className="h-4 w-4 mr-1" />
                            Share
                          </Button>
                        </DialogTrigger>
                        <DialogContent>
                          <DialogHeader>
                            <DialogTitle>Share Folder with Sections</DialogTitle>
                            <DialogDescription>
                              Select the sections you want to share this folder with
                            </DialogDescription>
                          </DialogHeader>
                          <div className="space-y-4">
                            {sections.length === 0 ? (
                              <p className="text-gray-500">No sections available</p>
                            ) : (
                              <div className="space-y-2 max-h-60 overflow-y-auto">
                                {sections.map((section) => (
                                  <div key={section.id} className="flex items-center space-x-2">
                                    <Checkbox
                                      id={`section-${section.id}`}
                                      checked={selectedSections.includes(section.id)}
                                      onCheckedChange={() => handleSectionToggle(section.id)}
                                      data-testid={`checkbox-section-${section.id}`}
                                    />
                                    <label 
                                      htmlFor={`section-${section.id}`}
                                      className="text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70"
                                    >
                                      {section.name} ({section.gradeLevel})
                                    </label>
                                  </div>
                                ))}
                              </div>
                            )}
                            <div className="flex justify-end gap-2">
                              <Button variant="outline" onClick={() => setShowShareDialog(false)}>
                                Cancel
                              </Button>
                              <Button 
                                onClick={handleShareFolder}
                                disabled={shareFolderMutation.isPending || selectedSections.length === 0}
                                data-testid="button-confirm-share"
                              >
                                {shareFolderMutation.isPending ? "Sharing..." : "Share"}
                              </Button>
                            </div>
                          </div>
                        </DialogContent>
                      </Dialog>

                      <Dialog open={showUploadDialog} onOpenChange={setShowUploadDialog}>
                        <DialogTrigger asChild>
                          <Button size="sm" data-testid="button-upload-document">
                            <Upload className="h-4 w-4 mr-1" />
                            Upload
                          </Button>
                        </DialogTrigger>
                        <DialogContent>
                          <DialogHeader>
                            <DialogTitle>Upload Document</DialogTitle>
                            <DialogDescription>
                              Add a new document to this folder
                            </DialogDescription>
                          </DialogHeader>
                          <div className="space-y-4">
                            <div>
                              <label className="text-sm font-medium">Document Name</label>
                              <Input
                                value={newDocumentName}
                                onChange={(e) => setNewDocumentName(e.target.value)}
                                placeholder="Enter document name"
                                data-testid="input-document-name"
                              />
                            </div>
                            <div>
                              <label className="text-sm font-medium">File URL</label>
                              <Input
                                value={newDocumentUrl}
                                onChange={(e) => setNewDocumentUrl(e.target.value)}
                                placeholder="Enter file URL (e.g., Google Drive link)"
                                data-testid="input-document-url"
                              />
                            </div>
                            <div className="flex justify-end gap-2">
                              <Button variant="outline" onClick={() => setShowUploadDialog(false)}>
                                Cancel
                              </Button>
                              <Button 
                                onClick={handleUploadDocument}
                                disabled={uploadDocumentMutation.isPending}
                                data-testid="button-save-document"
                              >
                                {uploadDocumentMutation.isPending ? "Uploading..." : "Upload"}
                              </Button>
                            </div>
                          </div>
                        </DialogContent>
                      </Dialog>
                    </div>
                  </div>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="flex items-center gap-4 text-sm text-gray-600">
                      <div className="flex items-center gap-1">
                        <Calendar className="h-4 w-4" />
                        Created: {new Date(selectedFolder.createdAt).toLocaleDateString()}
                      </div>
                      <div className="flex items-center gap-1">
                        <FileText className="h-4 w-4" />
                        {documents.length} documents
                      </div>
                    </div>

                    <div className="border-t pt-4">
                      <h3 className="font-medium mb-3">Documents</h3>
                      {documents.length === 0 ? (
                        <div className="text-center text-gray-500 py-8">
                          <FileText className="h-12 w-12 mx-auto mb-2 opacity-50" />
                          <p>No documents yet</p>
                          <p className="text-sm">Upload your first document to get started</p>
                        </div>
                      ) : (
                        <div className="space-y-2">
                          {documents.map((document) => (
                            <div
                              key={document.id}
                              className="flex items-center justify-between p-3 bg-gray-50 rounded-md"
                              data-testid={`document-item-${document.id}`}
                            >
                              <div className="flex items-center gap-3">
                                <FileText className="h-5 w-5 text-blue-600" />
                                <div>
                                  <h4 className="font-medium text-sm">{document.name}</h4>
                                  <p className="text-xs text-gray-500">
                                    Uploaded {new Date(document.uploadedAt).toLocaleDateString()}
                                  </p>
                                </div>
                              </div>
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => window.open(document.fileUrl, '_blank')}
                                data-testid={`button-view-document-${document.id}`}
                              >
                                <Eye className="h-4 w-4" />
                              </Button>
                            </div>
                          ))}
                        </div>
                      )}
                    </div>
                  </div>
                </CardContent>
              </Card>
            ) : (
              <Card>
                <CardContent className="flex flex-col items-center justify-center py-16">
                  <Folder className="h-16 w-16 text-gray-300 mb-4" />
                  <h3 className="text-lg font-medium text-gray-900 mb-2">No folder selected</h3>
                  <p className="text-gray-500 text-center">
                    Select a folder from the left to view its contents and manage documents
                  </p>
                </CardContent>
              </Card>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/teacher/GradeManag.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/contexts/AuthContext";
import { useToast } from "@/hooks/use-toast";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import {
  Users,
  GraduationCap,
  Award,
  BookOpen,
  Plus,
  Eye,
  Edit,
  Save,
  X,
} from "lucide-react";

// Form schema for grade encoding
const gradeFormSchema = z.object({
  studentId: z.number().min(1, "Student is required"),
  subjectId: z.number().min(1, "Subject is required"),
  grade: z.number().min(60).max(100, "Grade must be between 60-100"),
  quarter: z.enum(["1", "2", "3", "4"]),
  schoolYear: z.string().min(1, "School year is required"),
});

type GradeFormData = z.infer<typeof gradeFormSchema>;

interface Student {
  id: number;
  firstName: string;
  lastName: string;
  name: string;
  email: string;
}

interface Section {
  id: number;
  name: string;
  gradeLevel: number;
}

interface Subject {
  id: number;
  name: string;
}

interface Grade {
  id: number;
  studentId: number;
  subjectId: number;
  teacherId: number;
  grade: number;
  quarter: string;
  schoolYear: string;
  studentName: string;
  subjectName: string;
}

interface TeacherAssignment {
  id: number;
  sectionId: number;
  subjectId: number;
  sectionName: string;
  subjectName: string;
  gradeLevel: number;
}

export function GradeManagement() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedSection, setSelectedSection] = useState<number | null>(null);
  const [selectedQuarter, setSelectedQuarter] = useState<string>("1");
  const [editingGrade, setEditingGrade] = useState<{studentId: number, subjectId: number} | null>(null);
  const [tempGrade, setTempGrade] = useState<string>("");
  const [isGradeDialogOpen, setIsGradeDialogOpen] = useState(false);

  // Fetch teacher's assigned sections with subjects
  const { data: assignments = [] } = useQuery<TeacherAssignment[]>({
    queryKey: ["/api/teacher/assignments"],
    queryFn: () => apiRequest("/api/teacher/assignments")
  });

  // Fetch students in selected section
  const { data: students = [] } = useQuery<Student[]>({
    queryKey: ["/api/teacher/students", selectedSection],
    queryFn: () => apiRequest(`/api/teacher/students?sectionId=${selectedSection}`),
    enabled: !!selectedSection
  });

  // Fetch subjects for selected section
  const { data: subjects = [] } = useQuery<Subject[]>({
    queryKey: ["/api/teacher/section-subjects", selectedSection],
    queryFn: () => apiRequest(`/api/teacher/section-subjects?sectionId=${selectedSection}`),
    enabled: !!selectedSection
  });

  // Fetch grades for selected section and quarter
  const { data: grades = [] } = useQuery<Grade[]>({
    queryKey: ["/api/teacher/grades", selectedSection, selectedQuarter],
    queryFn: () => apiRequest(`/api/teacher/grades?sectionId=${selectedSection}&quarter=${selectedQuarter}`),
    enabled: !!selectedSection && !!selectedQuarter
  });

  // Grade form
  const gradeForm = useForm<GradeFormData>({
    resolver: zodResolver(gradeFormSchema),
    defaultValues: {
      quarter: "1",
      schoolYear: "2024-2025",
      grade: 75
    }
  });

  // Save grade mutation
  const saveGradeMutation = useMutation({
    mutationFn: (data: GradeFormData) => apiRequest("/api/teacher/grades", {
      method: "POST",
      body: JSON.stringify(data)
    }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/teacher/grades"] });
      toast({
        title: "Success",
        description: "Grade saved successfully"
      });
      setIsGradeDialogOpen(false);
      gradeForm.reset();
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to save grade",
        variant: "destructive"
      });
    }
  });

  // Update grade mutation
  const updateGradeMutation = useMutation({
    mutationFn: ({ studentId, subjectId, grade }: { studentId: number, subjectId: number, grade: number }) => 
      apiRequest(`/api/teacher/grades/${studentId}/${subjectId}`, {
        method: "PUT",
        body: JSON.stringify({ 
          grade, 
          quarter: selectedQuarter,
          schoolYear: "2024-2025"
        })
      }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/teacher/grades"] });
      setEditingGrade(null);
      setTempGrade("");
      toast({
        title: "Success",
        description: "Grade updated successfully"
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update grade",
        variant: "destructive"
      });
    }
  });

  // Get unique sections from assignments
  const sections = assignments.reduce((acc: Section[], assignment) => {
    const existingSection = acc.find(s => s.id === assignment.sectionId);
    if (!existingSection) {
      acc.push({
        id: assignment.sectionId,
        name: assignment.sectionName,
        gradeLevel: assignment.gradeLevel
      });
    }
    return acc;
  }, []);

  // Get grade for specific student and subject
  const getGrade = (studentId: number, subjectId: number) => {
    return grades.find(g => g.studentId === studentId && g.subjectId === subjectId);
  };

  // Handle grade submission
  const onSubmitGrade = (data: GradeFormData) => {
    saveGradeMutation.mutate(data);
  };

  // Handle inline grade editing
  const handleSaveInlineGrade = (studentId: number, subjectId: number) => {
    const grade = parseFloat(tempGrade);
    if (grade >= 60 && grade <= 100) {
      updateGradeMutation.mutate({ studentId, subjectId, grade });
    } else {
      toast({
        title: "Invalid Grade",
        description: "Grade must be between 60-100",
        variant: "destructive"
      });
    }
  };

  const startEditing = (studentId: number, subjectId: number, currentGrade?: number) => {
    setEditingGrade({ studentId, subjectId });
    setTempGrade(currentGrade?.toString() || "");
  };

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <GraduationCap className="h-5 w-5" />
            Grade Management
          </CardTitle>
          <CardDescription>
            Manage student grades for your assigned sections and subjects
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-6">
          {/* Section and Quarter Selection */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <label className="text-sm font-medium">Select Section</label>
              <Select
                value={selectedSection?.toString() || ""}
                onValueChange={(value) => setSelectedSection(parseInt(value))}
              >
                <SelectTrigger data-testid="select-section">
                  <SelectValue placeholder="Choose a section..." />
                </SelectTrigger>
                <SelectContent>
                  {sections.map((section) => (
                    <SelectItem key={section.id} value={section.id.toString()}>
                      {section.name} (Grade {section.gradeLevel})
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <label className="text-sm font-medium">Select Quarter</label>
              <Select
                value={selectedQuarter}
                onValueChange={setSelectedQuarter}
              >
                <SelectTrigger data-testid="select-quarter">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="1">1st Quarter</SelectItem>
                  <SelectItem value="2">2nd Quarter</SelectItem>
                  <SelectItem value="3">3rd Quarter</SelectItem>
                  <SelectItem value="4">4th Quarter</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          {selectedSection && (
            <Tabs defaultValue="gradesheet" className="space-y-4">
              <TabsList>
                <TabsTrigger value="gradesheet" className="flex items-center gap-2">
                  <Award className="h-4 w-4" />
                  Grade Sheet
                </TabsTrigger>
                <TabsTrigger value="students" className="flex items-center gap-2">
                  <Users className="h-4 w-4" />
                  Students ({students.length})
                </TabsTrigger>
              </TabsList>

              <TabsContent value="gradesheet" className="space-y-4">
                {students.length > 0 && subjects.length > 0 ? (
                  <div className="space-y-4">
                    <div className="flex justify-between items-center">
                      <h3 className="text-lg font-semibold">
                        {sections.find(s => s.id === selectedSection)?.name} - {selectedQuarter}
                        {selectedQuarter === "1" ? "st" : selectedQuarter === "2" ? "nd" : selectedQuarter === "3" ? "rd" : "th"} Quarter
                      </h3>
                      <Dialog open={isGradeDialogOpen} onOpenChange={setIsGradeDialogOpen}>
                        <DialogTrigger asChild>
                          <Button data-testid="add-grade-btn">
                            <Plus className="h-4 w-4 mr-2" />
                            Add Grade
                          </Button>
                        </DialogTrigger>
                        <DialogContent>
                          <DialogHeader>
                            <DialogTitle>Add New Grade</DialogTitle>
                            <DialogDescription>
                              Enter grade for a student in the selected section
                            </DialogDescription>
                          </DialogHeader>
                          <Form {...gradeForm}>
                            <form onSubmit={gradeForm.handleSubmit(onSubmitGrade)} className="space-y-4">
                              <FormField
                                control={gradeForm.control}
                                name="studentId"
                                render={({ field }) => (
                                  <FormItem>
                                    <FormLabel>Student</FormLabel>
                                    <FormControl>
                                      <Select
                                        value={field.value?.toString() || ""}
                                        onValueChange={(value) => field.onChange(parseInt(value))}
                                      >
                                        <SelectTrigger>
                                          <SelectValue placeholder="Select student..." />
                                        </SelectTrigger>
                                        <SelectContent>
                                          {students.map((student) => (
                                            <SelectItem key={student.id} value={student.id.toString()}>
                                              {student.firstName} {student.lastName}
                                            </SelectItem>
                                          ))}
                                        </SelectContent>
                                      </Select>
                                    </FormControl>
                                    <FormMessage />
                                  </FormItem>
                                )}
                              />

                              <FormField
                                control={gradeForm.control}
                                name="subjectId"
                                render={({ field }) => (
                                  <FormItem>
                                    <FormLabel>Subject</FormLabel>
                                    <FormControl>
                                      <Select
                                        value={field.value?.toString() || ""}
                                        onValueChange={(value) => field.onChange(parseInt(value))}
                                      >
                                        <SelectTrigger>
                                          <SelectValue placeholder="Select subject..." />
                                        </SelectTrigger>
                                        <SelectContent>
                                          {subjects.map((subject) => (
                                            <SelectItem key={subject.id} value={subject.id.toString()}>
                                              {subject.name}
                                            </SelectItem>
                                          ))}
                                        </SelectContent>
                                      </Select>
                                    </FormControl>
                                    <FormMessage />
                                  </FormItem>
                                )}
                              />

                              <FormField
                                control={gradeForm.control}
                                name="grade"
                                render={({ field }) => (
                                  <FormItem>
                                    <FormLabel>Grade (60-100)</FormLabel>
                                    <FormControl>
                                      <Input
                                        type="number"
                                        min="60"
                                        max="100"
                                        {...field}
                                        onChange={(e) => field.onChange(parseFloat(e.target.value))}
                                      />
                                    </FormControl>
                                    <FormMessage />
                                  </FormItem>
                                )}
                              />

                              <FormField
                                control={gradeForm.control}
                                name="quarter"
                                render={({ field }) => (
                                  <FormItem>
                                    <FormLabel>Quarter</FormLabel>
                                    <FormControl>
                                      <Select value={selectedQuarter} onValueChange={field.onChange}>
                                        <SelectTrigger>
                                          <SelectValue />
                                        </SelectTrigger>
                                        <SelectContent>
                                          <SelectItem value="1">1st Quarter</SelectItem>
                                          <SelectItem value="2">2nd Quarter</SelectItem>
                                          <SelectItem value="3">3rd Quarter</SelectItem>
                                          <SelectItem value="4">4th Quarter</SelectItem>
                                        </SelectContent>
                                      </Select>
                                    </FormControl>
                                    <FormMessage />
                                  </FormItem>
                                )}
                              />

                              <DialogFooter>
                                <Button
                                  type="button"
                                  variant="outline"
                                  onClick={() => setIsGradeDialogOpen(false)}
                                >
                                  Cancel
                                </Button>
                                <Button type="submit" disabled={saveGradeMutation.isPending}>
                                  {saveGradeMutation.isPending ? "Saving..." : "Save Grade"}
                                </Button>
                              </DialogFooter>
                            </form>
                          </Form>
                        </DialogContent>
                      </Dialog>
                    </div>

                    <div className="border rounded-lg overflow-hidden">
                      <Table>
                        <TableHeader>
                          <TableRow>
                            <TableHead className="w-[200px]">Student Name</TableHead>
                            {subjects.map((subject) => (
                              <TableHead key={subject.id} className="text-center">
                                {subject.name}
                              </TableHead>
                            ))}
                            <TableHead className="text-center">Average</TableHead>
                          </TableRow>
                        </TableHeader>
                        <TableBody>
                          {students.map((student) => {
                            const studentGrades = subjects.map(subject => getGrade(student.id, subject.id));
                            const validGrades = studentGrades.filter(g => g?.grade).map(g => g!.grade);
                            const average = validGrades.length > 0 
                              ? (validGrades.reduce((sum, grade) => sum + grade, 0) / validGrades.length).toFixed(1)
                              : "---";

                            return (
                              <TableRow key={student.id}>
                                <TableCell className="font-medium">
                                  {student.firstName} {student.lastName}
                                </TableCell>
                                {subjects.map((subject) => {
                                  const grade = getGrade(student.id, subject.id);
                                  const isEditing = editingGrade?.studentId === student.id && 
                                                  editingGrade?.subjectId === subject.id;

                                  return (
                                    <TableCell key={subject.id} className="text-center">
                                      {isEditing ? (
                                        <div className="flex items-center gap-1">
                                          <Input
                                            type="number"
                                            min="60"
                                            max="100"
                                            value={tempGrade}
                                            onChange={(e) => setTempGrade(e.target.value)}
                                            className="w-16 h-8 text-center"
                                            autoFocus
                                          />
                                          <Button
                                            size="sm"
                                            variant="ghost"
                                            className="h-8 w-8 p-0"
                                            onClick={() => handleSaveInlineGrade(student.id, subject.id)}
                                          >
                                            <Save className="h-3 w-3" />
                                          </Button>
                                          <Button
                                            size="sm"
                                            variant="ghost"
                                            className="h-8 w-8 p-0"
                                            onClick={() => setEditingGrade(null)}
                                          >
                                            <X className="h-3 w-3" />
                                          </Button>
                                        </div>
                                      ) : (
                                        <div 
                                          className="cursor-pointer hover:bg-gray-100 p-1 rounded flex items-center justify-center gap-1"
                                          onClick={() => startEditing(student.id, subject.id, grade?.grade)}
                                        >
                                          {grade ? (
                                            <Badge variant={grade.grade >= 75 ? "default" : "destructive"}>
                                              {grade.grade}
                                            </Badge>
                                          ) : (
                                            <span className="text-gray-400">---</span>
                                          )}
                                          <Edit className="h-3 w-3 opacity-50" />
                                        </div>
                                      )}
                                    </TableCell>
                                  );
                                })}
                                <TableCell className="text-center">
                                  <Badge variant={parseFloat(average) >= 75 ? "default" : "destructive"}>
                                    {average}
                                  </Badge>
                                </TableCell>
                              </TableRow>
                            );
                          })}
                        </TableBody>
                      </Table>
                    </div>
                  </div>
                ) : (
                  <div className="text-center py-12">
                    <BookOpen className="mx-auto h-12 w-12 text-gray-400" />
                    <h3 className="mt-2 text-sm font-semibold text-gray-900">No data available</h3>
                    <p className="mt-1 text-sm text-gray-500">
                      {students.length === 0 ? "No students enrolled in this section" : "No subjects assigned for this section"}
                    </p>
                  </div>
                )}
              </TabsContent>

              <TabsContent value="students" className="space-y-4">
                <div className="grid gap-4">
                  {students.length > 0 ? (
                    students.map((student) => (
                      <Card key={student.id}>
                        <CardContent className="p-4">
                          <div className="flex items-center justify-between">
                            <div>
                              <h4 className="font-semibold">{student.firstName} {student.lastName}</h4>
                              <p className="text-sm text-gray-500">{student.email}</p>
                            </div>
                            <Button variant="outline" size="sm">
                              <Eye className="h-4 w-4 mr-2" />
                              View Details
                            </Button>
                          </div>
                        </CardContent>
                      </Card>
                    ))
                  ) : (
                    <div className="text-center py-12">
                      <Users className="mx-auto h-12 w-12 text-gray-400" />
                      <h3 className="mt-2 text-sm font-semibold text-gray-900">No students enrolled</h3>
                      <p className="mt-1 text-sm text-gray-500">
                        There are no students enrolled in this section yet.
                      </p>
                    </div>
                  )}
                </div>
              </TabsContent>
            </Tabs>
          )}

          {!selectedSection && (
            <div className="text-center py-12">
              <GraduationCap className="mx-auto h-12 w-12 text-gray-400" />
              <h3 className="mt-2 text-sm font-semibold text-gray-900">Select a section to begin</h3>
              <p className="mt-1 text-sm text-gray-500">
                Choose a section from the dropdown above to view students and manage grades.
              </p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/teacher/RecentGrad.content:
import React from 'react';
import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/card';
import { Badge } from '@/components/ui/badge';
import { ClipboardCheck, TrendingUp, TrendingDown } from 'lucide-react';

export const RecentGrades: React.FC = () => {
  const { data: assignments } = useQuery({
    queryKey: ['/api/teacher/assignments'],
  });

  const { data: grades, isLoading } = useQuery({
    queryKey: ['/api/teacher/grades'],
    enabled: !!assignments && Array.isArray(assignments) && assignments.length > 0,
  });

  if (isLoading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <ClipboardCheck className="h-5 w-5" />
            Recent Grades
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="animate-pulse space-y-2">
            <div className="h-4 bg-gray-200 rounded w-3/4"></div>
            <div className="h-4 bg-gray-200 rounded w-1/2"></div>
            <div className="h-4 bg-gray-200 rounded w-2/3"></div>
          </div>
        </CardContent>
      </Card>
    );
  }

  if (!grades || grades.length === 0) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <ClipboardCheck className="h-5 w-5" />
            Recent Grades
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-center py-6">
            <ClipboardCheck className="mx-auto h-12 w-12 text-gray-400" />
            <h3 className="mt-2 text-sm font-semibold text-gray-900">No grades recorded</h3>
            <p className="mt-1 text-sm text-gray-500">
              Start encoding grades in the Grade Management tab.
            </p>
          </div>
        </CardContent>
      </Card>
    );
  }

  // Get the 5 most recent grades
  const recentGrades = grades.slice(-5).reverse();

  const getGradeColor = (grade: number) => {
    if (grade >= 90) return 'bg-green-100 text-green-800';
    if (grade >= 80) return 'bg-blue-100 text-blue-800';
    if (grade >= 75) return 'bg-yellow-100 text-yellow-800';
    return 'bg-red-100 text-red-800';
  };

  const getGradeIcon = (grade: number) => {
    return grade >= 75 ? TrendingUp : TrendingDown;
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <ClipboardCheck className="h-5 w-5" />
          Recent Grades Encoded
        </CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-3">
          {recentGrades.map((grade: any, index: number) => {
            const GradeIcon = getGradeIcon(grade.grade);
            return (
              <div
                key={`${grade.student_id}-${grade.subject_id}-${index}`}
                className="flex items-center justify-between p-3 bg-gray-50 rounded-lg"
              >
                <div className="flex-1">
                  <p className="font-medium text-gray-900">
                    {grade.student_name}
                  </p>
                  <p className="text-sm text-gray-500">
                    {grade.subject_name} â€¢ Quarter {grade.quarter}
                  </p>
                </div>
                <div className="flex items-center gap-2">
                  <Badge
                    className={`${getGradeColor(grade.grade)} flex items-center gap-1`}
                  >
                    <GradeIcon className="h-3 w-3" />
                    {grade.grade}
                  </Badge>
                </div>
              </div>
            );
          })}
        </div>
      </CardContent>
    </Card>
  );
};

